exports.john =
  { 1:  { 1:  [ {class: "vs", text: "In the beginning was the Word, and the Word was with God, and the Word was God."} ]
        , 2:  [ {class: "vs", text: "The same was in the beginning with God."} ]
        , 3:  [ {class: "vs", text: "All things were made through him. Without him, nothing was made that has been made."} ]
        , 4:  [ {class: "vs", text: "In him was life, and the life was the light of men."} ]
        , 5:  [ {class: "vs", text: "The light shines in the darkness, and the darkness hasn’t overcome it."} ]
        , 6:  [ {class: "vs", text: "There came a man, sent from God, whose name was John."} ]
        , 7:  [ {class: "vs", text: "The same came as a witness, that he might testify about the light, that all might believe through him."} ]
        , 8:  [ {class: "vs", text: "He was not the light, but was sent that he might testify about the light."} ]
        , 9:  [ {class: "vs", text: "The true light that enlightens everyone was coming into the world."} ]
        , 10: [ {class: "vs", text: "He was in the world, and the world was made through him, and the world didn’t recognize him."} ]
        , 11: [ {class: "vs", text: "He came to his own, and those who were his own didn’t receive him."} ]
        , 12: [ {class: "vs", text: "But as many as received him, to them he gave the right to become God’s children, to those who believe in his name:"} ]
        , 13: [ {class: "vs", text: "who were born not of blood, nor of the will of the flesh, nor of the will of man, but of God."} ]
        , 14: [ {class: "vs", text: "The Word became flesh, and lived among us. We saw his glory, such glory as of the one and only Son of the Father, full of grace and truth."} ]
        , 15: [ {class: "vs", text: "John testified about him. He cried out, saying, “This was he of whom I said, ‘He who comes after me has surpassed me, for he was before me.’”"} ]
        , 16: [ {class: "vs", text: "From his fullness we all received grace upon grace."} ]
        , 17: [ {class: "vs", text: "For the law was given through Moses. Grace and truth were realized through Jesus Christ."} ]
        , 18: [ {class: "vs", text: "No one has seen God at any time. The one and only Son, who is in the bosom of the Father, has declared him."} ]
        , 19: [ {class: "vs", text: "This is John’s testimony, when the Jews sent priests and Levites from Jerusalem to ask him, “Who are you?”"} ]
        , 20: [ {class: "vs", text: "He declared, and didn’t deny, but he declared, “I am not the Christ.”"} ]
        , 21: [ {class: "vs", text: "They asked him, “What then? Are you Elijah?”</p><p> He said, “I am not.”</p><p> “Are you the prophet?”</p><p> He answered, “No.”"} ]
        , 22: [ {class: "vs", text: "They said therefore to him, “Who are you? Give us an answer to take back to those who sent us. What do you say about yourself?”"} ]
        , 23: [ {class: "vs", text: "He said, “I am the voice of one crying in the wilderness, ‘Make straight the way of the Lord,’ as Isaiah the prophet said.”"} ]
        , 24: [ {class: "vs", text: "The ones who had been sent were from the Pharisees."} ]
        , 25: [ {class: "vs", text: "They asked him, “Why then do you baptize, if you are not the Christ, nor Elijah, nor the prophet?”"} ]
        , 26: [ {class: "vs", text: "John answered them, “I baptize in water, but among you stands one whom you don’t know."} ]
        , 27: [ {class: "vs", text: "He is the one who comes after me, who is preferred before me, whose sandal strap I’m not worthy to loosen.”"} ]
        , 28: [ {class: "vs", text: "These things were done in Bethany beyond the Jordan, where John was baptizing."} ]
        , 29: [ {class: "vs", text: "The next day, he saw Jesus coming to him, and said, “Behold, the Lamb of God, who takes away the sin of the world!"} ]
        , 30: [ {class: "vs", text: "This is he of whom I said, ‘After me comes a man who is preferred before me, for he was before me.’"} ]
        , 31: [ {class: "vs", text: "I didn’t know him, but for this reason I came baptizing in water: that he would be revealed to Israel.”"} ]
        , 32: [ {class: "vs", text: "John testified, saying, “I have seen the Spirit descending like a dove out of heaven, and it remained on him."} ]
        , 33: [ {class: "vs", text: "I didn’t recognize him, but he who sent me to baptize in water said to me, ‘On whomever you will see the Spirit descending and remaining on him is he who baptizes in the Holy Spirit.’"} ]
        , 34: [ {class: "vs", text: "I have seen, and have testified that this is the Son of God.”"} ]
        , 35: [ {class: "vs", text: "Again, the next day, John was standing with two of his disciples,"} ]
        , 36: [ {class: "vs", text: "and he looked at Jesus as he walked, and said, “Behold, the Lamb of God!”"} ]
        , 37: [ {class: "vs", text: "The two disciples heard him speak, and they followed Jesus."} ]
        , 38: [ {class: "vs", text: "Jesus turned and saw them following, and said to them,"}
              , {class: "vs", wj: true, text: "“What are you looking for?”</span></p><p> They said to him, “Rabbi” (which is to say, being interpreted, Teacher), “where are you staying?”"}
              ]
        , 39: [ {class: "vs", text: "He said to them,"}
              , {class: "vs", wj: true, text: "“Come, and see.”</span></p><p> They came and saw where he was staying, and they stayed with him that day. It was about the tenth hour."}
              ]
        , 40: [ {class: "vs", text: "One of the two who heard John and followed him was Andrew, Simon Peter’s brother."} ]
        , 41: [ {class: "vs", text: "He first found his own brother, Simon, and said to him, “We have found the Messiah!” (which is, being interpreted, Christ )."} ]
        , 42: [ {class: "vs", text: "He brought him to Jesus. Jesus looked at him, and said,"}
              , {class: "vs", wj: true, text: "“You are Simon the son of Jonah. You shall be called Cephas”</span> (which is by interpretation, Peter)."}
              ]
        , 43: [ {class: "vs", text: "On the next day, he was determined to go out into Galilee, and he found Philip. Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Follow me.”"}
              ]
        , 44: [ {class: "vs", text: "Now Philip was from Bethsaida, of the city of Andrew and Peter."} ]
        , 45: [ {class: "vs", text: "Philip found Nathanael, and said to him, “We have found him, of whom Moses in the law, and the prophets, wrote: Jesus of Nazareth, the son of Joseph.”"} ]
        , 46: [ {class: "vs", text: "Nathanael said to him, “Can any good thing come out of Nazareth?”</p><p> Philip said to him, “Come and see.”"} ]
        , 47: [ {class: "vs", text: "Jesus saw Nathanael coming to him, and said about him,"}
              , {class: "vs", wj: true, text: "“Behold, an Israelite indeed, in whom is no deceit!”"}
              ]
        , 48: [ {class: "vs", text: "Nathanael said to him, “How do you know me?”</p><p> Jesus answered him,"}
              , {class: "vs", wj: true, text: "“Before Philip called you, when you were under the fig tree, I saw you.”"}
              ]
        , 49: [ {class: "vs", text: "Nathanael answered him, “Rabbi, you are the Son of God! You are King of Israel!”"} ]
        , 50: [ {class: "vs", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“Because I told you, ‘I saw you underneath the fig tree,’ do you believe? You will see greater things than these!”"}
              ]
        , 51: [ {class: "vs", text: "He said to him,"}
              , {class: "vs", wj: true, text: "“Most certainly, I tell you all, hereafter you will see heaven opened, and the angels of God ascending and descending on the Son of Man.”"}
              ]
        }
  , 10: { 1:  [ {class: "vs", wj: true, text: "“Most certainly, I tell you, one who doesn’t enter by the door into the sheep fold, but climbs up some other way, is a thief and a robber."} ]
        , 2:  [ {class: "vs", wj: true, text: "But one who enters in by the door is the shepherd of the sheep."} ]
        , 3:  [ {class: "vs", wj: true, text: "The gatekeeper opens the gate for him, and the sheep listen to his voice. He calls his own sheep by name, and leads them out."} ]
        , 4:  [ {class: "vs", wj: true, text: "Whenever he brings out his own sheep, he goes before them, and the sheep follow him, for they know his voice."} ]
        , 5:  [ {class: "vs", wj: true, text: "They will by no means follow a stranger, but will flee from him; for they don’t know the voice of strangers.”"} ]
        , 6:  [ {class: "vs", text: "Jesus spoke this parable to them, but they didn’t understand what he was telling them."} ]
        , 7:  [ {class: "endpara", text: "Jesus therefore said to them again,"}
              , {class: "vs", wj: true, text: "“Most certainly, I tell you, I am the sheep’s door."}
              ]
        , 8:  [ {class: "vs", wj: true, text: "All who came before me are thieves and robbers, but the sheep didn’t listen to them."} ]
        , 9:  [ {class: "vs", wj: true, text: "I am the door. If anyone enters in by me, he will be saved, and will go in and go out, and will find pasture."} ]
        , 10: [ {class: "vs", wj: true, text: "The thief only comes to steal, kill, and destroy. I came that they may have life, and may have it abundantly."} ]
        , 11: [ {class: "vs", wj: true, text: "I am the good shepherd."}
              , {class: "vs", wj: true, text: "The good shepherd lays down his life for the sheep."}
              ]
        , 12: [ {class: "vs", wj: true, text: "He who is a hired hand, and not a shepherd, who doesn’t own the sheep, sees the wolf coming, leaves the sheep, and flees. The wolf snatches the sheep, and scatters them."} ]
        , 13: [ {class: "vs", wj: true, text: "The hired hand flees because he is a hired hand, and doesn’t care for the sheep."} ]
        , 14: [ {class: "vs", wj: true, text: "I am the good shepherd. I know my own, and I’m known by my own;"} ]
        , 15: [ {class: "vs", wj: true, text: "even as the Father knows me, and I know the Father. I lay down my life for the sheep."} ]
        , 16: [ {class: "vs", wj: true, text: "I have other sheep, which are not of this fold."}
              , {class: "vs", wj: true, text: "I must bring them also, and they will hear my voice. They will become one flock with one shepherd."}
              ]
        , 17: [ {class: "vs", wj: true, text: "Therefore the Father loves me, because I lay down my life,"}
              , {class: "vs", wj: true, text: "that I may take it again."}
              ]
        , 18: [ {class: "vs", wj: true, text: "No one takes it away from me, but I lay it down by myself. I have power to lay it down, and I have power to take it again. I received this commandment from my Father.”"} ]
        , 19: [ {class: "vs", text: "Therefore a division arose again among the Jews because of these words."} ]
        , 20: [ {class: "vs", text: "Many of them said, “He has a demon, and is insane! Why do you listen to him?”"} ]
        , 21: [ {class: "endpara", text: "Others said, “These are not the sayings of one possessed by a demon. It isn’t possible for a demon to open the eyes of the blind, is it?”"} ]
        , 22: [ {class: "endpara", text: "It was the Feast of the Dedication at Jerusalem."} ]
        , 23: [ {class: "vs", text: "It was winter, and Jesus was walking in the temple, in Solomon’s porch."} ]
        , 24: [ {class: "vs", text: "The Jews therefore came around him and said to him, “How long will you hold us in suspense? If you are the Christ, tell us plainly.”"} ]
        , 25: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“I told you, and you don’t believe. The works that I do in my Father’s name, these testify about me."}
              ]
        , 26: [ {class: "vs", wj: true, text: "But you don’t believe, because you are not of my sheep, as I told you."} ]
        , 27: [ {class: "vs", wj: true, text: "My sheep hear my voice, and I know them, and they follow me."} ]
        , 28: [ {class: "vs", wj: true, text: "I give eternal life to them. They will never perish, and no one will snatch them out of my hand."} ]
        , 29: [ {class: "vs", wj: true, text: "My Father who has given them to me is greater than all. No one is able to snatch them out of my Father’s hand."} ]
        , 30: [ {class: "vs", wj: true, text: "I and the Father are one.”"} ]
        , 31: [ {class: "vs", text: "Therefore Jews took up stones again to stone him."} ]
        , 32: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“I have shown you many good works from my Father. For which of those works do you stone me?”"}
              ]
        , 33: [ {class: "vs", text: "The Jews answered him, “We don’t stone you for a good work, but for blasphemy: because you, being a man, make yourself God.”"} ]
        , 34: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Isn’t it written in your law, ‘I said, you are gods?’"}
              ]
        , 35: [ {class: "vs", wj: true, text: "If he called them gods, to whom the word of God came (and the Scripture can’t be broken),"} ]
        , 36: [ {class: "vs", wj: true, text: "do you say of him whom the Father sanctified and sent into the world, ‘You blaspheme,’ because I said, ‘I am the Son of God?’"} ]
        , 37: [ {class: "vs", wj: true, text: "If I don’t do the works of my Father, don’t believe me."} ]
        , 38: [ {class: "vs", wj: true, text: "But if I do them, though you don’t believe me, believe the works, that you may know and believe that the Father is in me, and I in the Father.”"} ]
        , 39: [ {class: "vs", text: "They sought again to seize him, and he went out of their hand."} ]
        , 40: [ {class: "vs", text: "He went away again beyond the Jordan into the place where John was baptizing at first, and he stayed there."} ]
        , 41: [ {class: "vs", text: "Many came to him. They said, “John indeed did no sign, but everything that John said about this man is true.”"} ]
        , 42: [ {class: "vs", text: "Many believed in him there."} ]
        }
  , 11: { 1:  [ {class: "vs", text: "Now a certain man was sick, Lazarus from Bethany, of the village of Mary and her sister, Martha."} ]
        , 2:  [ {class: "vs", text: "It was that Mary who had anointed the Lord with ointment and wiped his feet with her hair, whose brother, Lazarus, was sick."} ]
        , 3:  [ {class: "vs", text: "The sisters therefore sent to him, saying, “Lord, behold, he for whom you have great affection is sick.”"} ]
        , 4:  [ {class: "vs", text: "But when Jesus heard it, he said,"}
              , {class: "vs", wj: true, text: "“This sickness is not to death, but for the glory of God, that God’s Son may be glorified by it.”"}
              ]
        , 5:  [ {class: "vs", text: "Now Jesus loved Martha, and her sister, and Lazarus."} ]
        , 6:  [ {class: "vs", text: "When therefore he heard that he was sick, he stayed two days in the place where he was."} ]
        , 7:  [ {class: "vs", text: "Then after this he said to the disciples,"}
              , {class: "vs", wj: true, text: "“Let’s go into Judea again.”"}
              ]
        , 8:  [ {class: "vs", text: "The disciples asked him, “Rabbi, the Jews were just trying to stone you. Are you going there again?”"} ]
        , 9:  [ {class: "vs", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“Aren’t there twelve hours of daylight? If a man walks in the day, he doesn’t stumble, because he sees the light of this world."}
              ]
        , 10: [ {class: "vs", wj: true, text: "But if a man walks in the night, he stumbles, because the light isn’t in him.”"} ]
        , 11: [ {class: "vs", text: "He said these things, and after that, he said to them,"}
              , {class: "vs", wj: true, text: "“Our friend, Lazarus, has fallen asleep, but I am going so that I may awake him out of sleep.”"}
              ]
        , 12: [ {class: "vs", text: "The disciples therefore said, “Lord, if he has fallen asleep, he will recover.”"} ]
        , 13: [ {class: "vs", text: "Now Jesus had spoken of his death, but they thought that he spoke of taking rest in sleep."} ]
        , 14: [ {class: "endpara", text: "So Jesus said to them plainly then,"}
              , {class: "vs", wj: true, text: "“Lazarus is dead."}
              ]
        , 15: [ {class: "vs", wj: true, text: "I am glad for your sakes that I was not there, so that you may believe. Nevertheless, let’s go to him.”"} ]
        , 16: [ {class: "vs", text: "Thomas therefore, who is called Didymus, said to his fellow disciples, “Let’s go also, that we may die with him.”"} ]
        , 17: [ {class: "vs", text: "So when Jesus came, he found that he had been in the tomb four days already."} ]
        , 18: [ {class: "vs", text: "Now Bethany was near Jerusalem, about fifteen stadia away."} ]
        , 19: [ {class: "vs", text: "Many of the Jews had joined the women around Martha and Mary, to console them concerning their brother."} ]
        , 20: [ {class: "vs", text: "Then when Martha heard that Jesus was coming, she went and met him, but Mary stayed in the house."} ]
        , 21: [ {class: "vs", text: "Therefore Martha said to Jesus, “Lord, if you would have been here, my brother wouldn’t have died."} ]
        , 22: [ {class: "vs", text: "Even now I know that whatever you ask of God, God will give you.”"} ]
        , 23: [ {class: "vs", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Your brother will rise again.”"}
              ]
        , 24: [ {class: "vs", text: "Martha said to him, “I know that he will rise again in the resurrection at the last day.”"} ]
        , 25: [ {class: "para", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“I am the resurrection and the life. He who believes in me will still live, even if he dies."} 
              ]
        , 26: [ {class: "vs", wj: true, text: "Whoever lives and believes in me will never die. Do you believe this?”"} ]
        , 27: [ {class: "vs", text: "She said to him, “Yes, Lord. I have come to believe that you are the Christ, God’s Son, he who comes into the world.”"} ]
        , 28: [ {class: "vs", text: "When she had said this, she went away and called Mary, her sister, secretly, saying, “The Teacher is here and is calling you.”"} ]
        , 29: [ {class: "vs", text: "When she heard this, she arose quickly and went to him."} ]
        , 30: [ {class: "vs", text: "Now Jesus had not yet come into the village, but was in the place where Martha met him."} ]
        , 31: [ {class: "vs", text: "Then the Jews who were with her in the house and were consoling her, when they saw Mary, that she rose up quickly and went out, followed her, saying, “She is going to the tomb to weep there.”"} ]
        , 32: [ {class: "vs", text: "Therefore when Mary came to where Jesus was and saw him, she fell down at his feet, saying to him, “Lord, if you would have been here, my brother wouldn’t have died.”"} ]
        , 33: [ {class: "vs", text: "When Jesus therefore saw her weeping, and the Jews weeping who came with her, he groaned in the spirit, and was troubled,"} ]
        , 34: [ {class: "vs", text: "and said,"}
              , {class: "vs", wj: true, text: "“Where have you laid him?”</span></p><p> They told him, “Lord, come and see.”"}
              ]
        , 35: [ {class: "vs", text: "Jesus wept."} ]
        , 36: [ {class: "vs", text: "The Jews therefore said, “See how much affection he had for him!”"} ]
        , 37: [ {class: "vs", text: "Some of them said, “Couldn’t this man, who opened the eyes of him who was blind, have also kept this man from dying?”"} ]
        , 38: [ {class: "vs", text: "Jesus therefore, again groaning in himself, came to the tomb. Now it was a cave, and a stone lay against it."} ]
        , 39: [ {class: "endpara", text: "Jesus said,"}
              , {class: "vs", wj: true, text: "“Take away the stone.”"}
              , {class: "para", text: "Martha, the sister of him who was dead, said to him, “Lord, by this time there is a stench, for he has been dead four days.”"}
              ]
        , 40: [ {class: "", text: "Jesus said to her,"}
              , {class: "“Didn’t I tell you that if you believed, you would see God’s glory?”"}
              ]
        , 41: [ {class: "endpara", text: "So they took away the stone from the place where the dead man was lying. Jesus lifted up his eyes, and said,"}
              , {class: "vs", wj: true, text: "“Father, I thank you that you listened to me."}
              ]
        , 42: [ {class: "vs", wj: true, text: "I know that you always listen to me, but because of the multitude standing around I said this, that they may believe that you sent me.”"} ]
        , 43: [ {class: "endpara", text: "When he had said this, he cried with a loud voice,"}
              , {class: "vs", wj: true, text: "“Lazarus, come out!”"}
              ]
        , 44: [ {class: "endpara", text: "He who was dead came out, bound hand and foot with wrappings, and his face was wrapped around with a cloth."}
              , {class: "endpara", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“Free him, and let him go.”"}
              ]
        , 45: [ {class: "vs", text: "Therefore many of the Jews who came to Mary and saw what Jesus did believed in him."} ]
        , 46: [ {class: "vs", text: "But some of them went away to the Pharisees and told them the things which Jesus had done."} ]
        , 47: [ {class: "vs", text: "The chief priests therefore and the Pharisees gathered a council, and said, “What are we doing? For this man does many signs."} ]
        , 48: [ {class: "vs", text: "If we leave him alone like this, everyone will believe in him, and the Romans will come and take away both our place and our nation.”"} ]
        , 49: [ {class: "vs", text: "But a certain one of them, Caiaphas, being high priest that year, said to them, “You know nothing at all,"} ]
        , 50: [ {class: "vs", text: "nor do you consider that it is advantageous for us that one man should die for the people, and that the whole nation not perish.”"} ]
        , 51: [ {class: "vs", text: "Now he didn’t say this of himself, but being high priest that year, he prophesied that Jesus would die for the nation,"} ]
        , 52: [ {class: "vs", text: "and not for the nation only, but that he might also gather together into one the children of God who are scattered abroad."} ]
        , 53: [ {class: "vs", text: "So from that day forward they took counsel that they might put him to death."} ]
        , 54: [ {class: "vs", text: "Jesus therefore walked no more openly among the Jews, but departed from there into the country near the wilderness, to a city called Ephraim. He stayed there with his disciples."} ]
        , 55: [ {class: "vs", text: "Now the Passover of the Jews was at hand. Many went up from the country to Jerusalem before the Passover, to purify themselves."} ]
        , 56: [ {class: "vs", text: "Then they sought for Jesus and spoke with one another as they stood in the temple, “What do you think—that he isn’t coming to the feast at all?”"} ]
        , 57: [ {class: "vs", text: "Now the chief priests and the Pharisees had commanded that if anyone knew where he was, he should report it, that they might seize him."} ]
        }
  , 12: { 1:  [ {class: "vs", text: "Then six days before the Passover, Jesus came to Bethany, where Lazarus was, who had been dead, whom he raised from the dead."} ]
        , 2:  [ {class: "vs", text: "So they made him a supper there. Martha served, but Lazarus was one of those who sat at the table with him."} ]
        , 3:  [ {class: "vs", text: "Therefore Mary took a pound of ointment of pure nard, very precious, and anointed Jesus’s feet and wiped his feet with her hair. The house was filled with the fragrance of the ointment."} ]
        , 4:  [ {class: "vs", text: "Then Judas Iscariot, Simon’s son, one of his disciples, who would betray him, said,"} ]
        , 5:  [ {class: "vs", text: "“Why wasn’t this ointment sold for three hundred denarii, and given to the poor?”"} ]
        , 6:  [ {class: "vs", text: "Now he said this, not because he cared for the poor, but because he was a thief, and having the money box, used to steal what was put into it."} ]
        , 7:  [ {class: "endpara", text: "But Jesus said,"}
              , {class: "vs", wj: true, text: "“Leave her alone. She has kept this for the day of my burial."}
              ]
        , 8:  [ {class: "vs", wj: true, text: "For you always have the poor with you, but you don’t always have me.”"} ]
        , 9:  [ {class: "vs", text: "A large crowd therefore of the Jews learned that he was there, and they came, not for Jesus’ sake only, but that they might see Lazarus also, whom he had raised from the dead."} ]
        , 10: [ {class: "vs", text: "But the chief priests conspired to put Lazarus to death also,"} ]
        , 11: [ {class: "vs", text: "because on account of him many of the Jews went away and believed in Jesus."} ]
        , 12: [ {class: "vs", text: "On the next day a great multitude had come to the feast. When they heard that Jesus was coming to Jerusalem,"} ]
        , 13: [ {class: "vs", text: "they took the branches of the palm trees and went out to meet him, and cried out, “Hosanna! Blessed is he who comes in the name of the Lord, the King of Israel!”"} ]
        , 14: [ {class: "vs", text: "Jesus, having found a young donkey, sat on it. As it is written,"} ]
        , 15: [ {class: "vs", text: "“Don’t be afraid, daughter of Zion. Behold, your King comes, sitting on a donkey’s colt.”"} ]
        , 16: [ {class: "vs", text: "His disciples didn’t understand these things at first, but when Jesus was glorified, then they remembered that these things were written about him, and that they had done these things to him."} ]
        , 17: [ {class: "vs", text: "The multitude therefore that was with him when he called Lazarus out of the tomb and raised him from the dead was testifying about it."} ]
        , 18: [ {class: "vs", text: "For this cause also the multitude went and met him, because they heard that he had done this sign."} ]
        , 19: [ {class: "vs", text: "The Pharisees therefore said among themselves, “See how you accomplish nothing. Behold, the world has gone after him.”"} ]
        , 20: [ {class: "vs", text: "Now there were certain Greeks among those that went up to worship at the feast."} ]
        , 21: [ {class: "vs", text: "These, therefore, came to Philip, who was from Bethsaida of Galilee, and asked him, saying, “Sir, we want to see Jesus.”"} ]
        , 22: [ {class: "vs", text: "Philip came and told Andrew, and in turn, Andrew came with Philip, and they told Jesus."} ]
        , 23: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“The time has come for the Son of Man to be glorified."}
              ]
        , 24: [ {class: "vs", wj: true, text: "Most certainly I tell you, unless a grain of wheat falls into the earth and dies, it remains by itself alone. But if it dies, it bears much fruit."} ]
        , 25: [ {class: "vs", wj: true, text: "He who loves his life will lose it. He who hates his life in this world will keep it to eternal life."} ]
        , 26: [ {class: "vs", wj: true, text: "If anyone serves me, let him follow me. Where I am, there my servant will also be. If anyone serves me, the Father will honor him."} ]
        , 27: [ {class: "vs", wj: true, text: "“Now my soul is troubled. What shall I say? ‘Father, save me from this time?’ But I came to this time for this cause."} ]
        , 28: [ {class: "vs", wj: true, text: "Father, glorify your name!”</span></p><p> Then a voice came out of the sky, saying, “I have both glorified it, and will glorify it again.”"} ]
        , 29: [ {class: "vs", text: "Therefore the multitude who stood by and heard it said that it had thundered. Others said, “An angel has spoken to him.”"} ]
        , 30: [ {class: "endpara", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“This voice hasn’t come for my sake, but for your sakes."}
              ]
        , 31: [ {class: "vs", wj: true, text: "Now is the judgment of this world. Now the prince of this world will be cast out."} ]
        , 32: [ {class: "vs", wj: true, text: "And I, if I am lifted up from the earth, will draw all people to myself.”"} ]
        , 33: [ {class: "vs", text: "But he said this, signifying by what kind of death he should die."} ]
        , 34: [ {class: "endpara", text: "The multitude answered him, “We have heard out of the law that the Christ remains forever. How do you say,"}
              , {class: "vs", wj: true, text: "‘The Son of Man must be lifted up?’</span> Who is this Son of Man?”"}
              ]
        , 35: [ {class: "endpara", text: "Jesus therefore said to them,"}
              , {class: "vs", wj: true, text: "“Yet a little while the light is with you. Walk while you have the light, that darkness doesn’t overtake you. He who walks in the darkness doesn’t know where he is going."}
              ]
        , 36: [ {class: "vs", wj: true, text: "While you have the light, believe in the light, that you may become children of light.”</span> Jesus said these things, and he departed and hid himself from them."} ]
        , 37: [ {class: "vs", text: "But though he had done so many signs before them, yet they didn’t believe in him,"} ]
        , 38: [ {class: "endpara", text: "that the word of Isaiah the prophet might be fulfilled, which he spoke,"}
              , {class: "q", text: "“Lord, who has believed our report?"}
              , {class: "q2", text: "To whom has the arm of the Lord been revealed?”"}
              ]
        , 39: [ {class: "vs", text: "For this cause they couldn’t believe, for Isaiah said again,"} ]
        , 40: [ {class: "q", text: "“He has blinded their eyes and he hardened their heart,"}
              , {class: "q2", text: "lest they should see with their eyes,"}
              , {class: "q2", text: "and perceive with their heart,"}
              , {class: "q2", text: "and would turn,"}
              , {class: "q2", text: "and I would heal them.”"}
              ]
        , 41: [ {class: "vs", text: "Isaiah said these things when he saw his glory, and spoke of him."} ]
        , 42: [ {class: "vs", text: "Nevertheless even many of the rulers believed in him, but because of the Pharisees they didn’t confess it, so that they wouldn’t be put out of the synagogue,"} ]
        , 43: [ {class: "vs", text: "for they loved men’s praise more than God’s praise."} ]
        , 44: [ {class: "endpara", text: "Jesus cried out and said,"}
              , {class: "vs", wj: true, text: "“Whoever believes in me, believes not in me, but in him who sent me."}
              ]
        , 45: [ {class: "vs", wj: true, text: "He who sees me sees him who sent me."} ]
        , 46: [ {class: "vs", wj: true, text: "I have come as a light into the world, that whoever believes in me may not remain in the darkness."} ]
        , 47: [ {class: "vs", wj: true, text: "If anyone listens to my sayings, and doesn’t believe, I don’t judge him. For I came not to judge the world, but to save the world."} ]
        , 48: [ {class: "vs", wj: true, text: "He who rejects me, and doesn’t receive my sayings, has one who judges him. The word that I spoke will judge him in the last day."} ]
        , 49: [ {class: "vs", wj: true, text: "For I spoke not from myself, but the Father who sent me, he gave me a commandment, what I should say, and what I should speak."} ]
        , 50: [ {class: "vs", wj: true, text: "I know that his commandment is eternal life. The things therefore which I speak, even as the Father has said to me, so I speak.”"} ]
        }
  , 13: { 1:  [ {class: "vs", text: "Now before the feast of the Passover, Jesus, knowing that his time had come that he would depart from this world to the Father, having loved his own who were in the world, he loved them to the end."} ]
        , 2:  [ {class: "vs", text: "During supper, the devil having already put into the heart of Judas Iscariot, Simon’s son, to betray him,"} ]
        , 3:  [ {class: "vs", text: "Jesus, knowing that the Father had given all things into his hands, and that he came from God, and was going to God,"} ]
        , 4:  [ {class: "vs", text: "arose from supper, and laid aside his outer garments. He took a towel and wrapped a towel around his waist."} ]
        , 5:  [ {class: "vs", text: "Then he poured water into the basin, and began to wash the disciples’ feet and to wipe them with the towel that was wrapped around him."} ]
        , 6:  [ {class: "vs", text: "Then he came to Simon Peter. He said to him, “Lord, do you wash my feet?”"} ]
        , 7:  [ {class: "endpara", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“You don’t know what I am doing now, but you will understand later.”"}
              ]
        , 8:  [ {class: "para", text: "Peter said to him, “You will never wash my feet!”"}
              , {class: "para", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“If I don’t wash you, you have no part with me.”"}
              ]
        , 9:  [ {class: "vs", text: "Simon Peter said to him, “Lord, not my feet only, but also my hands and my head!”"} ]
        , 10: [ {class: "vs", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Someone who has bathed only needs to have his feet washed, but is completely clean. You are clean, but not all of you.”"}
              ]
        , 11: [ {class: "vs", text: "For he knew him who would betray him, therefore he said,"}
              , {class: "vs", wj: true, text: "“You are not all clean.”"}
              ]
        , 12: [ {class: "vs", text: "So when he had washed their feet, put his outer garment back on, and sat down again, he said to them,"}
              , {class: "vs", wj: true, text: "“Do you know what I have done to you?"}
              ]
        , 13: [ {class: "vs", wj: true, text: "You call me, ‘Teacher’ and ‘Lord.’ You say so correctly, for so I am."} ]
        , 14: [ {class: "vs", wj: true, text: "If I then, the Lord and the Teacher, have washed your feet, you also ought to wash one another’s feet."} ]
        , 15: [ {class: "vs", wj: true, text: "For I have given you an example, that you should also do as I have done to you."} ]
        , 16: [ {class: "vs", wj: true, text: "Most certainly I tell you, a servant is not greater than his lord, neither is one who is sent greater than he who sent him."} ]
        , 17: [ {class: "vs", wj: true, text: "If you know these things, blessed are you if you do them."} ]
        , 18: [ {class: "vs", wj: true, text: "I don’t speak concerning all of you. I know whom I have chosen. But that the Scripture may be fulfilled, ‘He who eats bread with me has lifted up his heel against me.’"} ]
        , 19: [ {class: "vs", wj: true, text: "From now on, I tell you before it happens, that when it happens, you may believe that I am he."} ]
        , 20: [ {class: "vs", wj: true, text: "Most certainly I tell you, he who receives whomever I send, receives me; and he who receives me, receives him who sent me.”"} ]
        , 21: [ {class: "vs", text: "When Jesus had said this, he was troubled in spirit, and testified,"}
              , {class: "vs", wj: true, text: "“Most certainly I tell you that one of you will betray me.”"}
              ]
        , 22: [ {class: "vs", text: "The disciples looked at one another, perplexed about whom he spoke."} ]
        , 23: [ {class: "vs", text: "One of his disciples, whom Jesus loved, was at the table, leaning against Jesus’ breast."} ]
        , 24: [ {class: "vs", text: "Simon Peter therefore beckoned to him, and said to him, “Tell us who it is of whom he speaks.”"} ]
        , 25: [ {class: "vs", text: "He, leaning back, as he was, on Jesus’ breast, asked him, “Lord, who is it?”"} ]
        , 26: [ {class: "vs", text: "Jesus therefore answered,"}
              , {class: "vs", wj: true, text: "“It is he to whom I will give this piece of bread when I have dipped it.”</span> So when he had dipped the piece of bread, he gave it to Judas, the son of Simon Iscariot."}
              ]
        , 27: [ {class: "", text: "After the piece of bread, then Satan entered into him.</p><p> Then Jesus said to him,"}
              , {class: "vs", wj: true, text: "“What you do, do quickly.”"}
              ]
        , 28: [ {class: "vs", text: "Now nobody at the table knew why he said this to him."} ]
        , 29: [ {class: "vs", text: "For some thought, because Judas had the money box, that Jesus said to him, “Buy what things we need for the feast,” or that he should give something to the poor."} ]
        , 30: [ {class: "vs", text: "Therefore having received that morsel, he went out immediately. It was night."} ]
        , 31: [ {class: "", text: "When he had gone out, Jesus said,"}
              , {class: "vs", wj: true, text: "“Now the Son of Man has been glorified, and God has been glorified in him."}
              ]
        , 32: [ {class: "vs", wj: true, text: "If God has been glorified in him, God will also glorify him in himself, and he will glorify him immediately."} ]
        , 33: [ {class: "vs", wj: true, text: "Little children, I will be with you a little while longer. You will seek me, and as I said to the Jews, ‘Where I am going, you can’t come,’ so now I tell you."} ]
        , 34: [ {class: "vs", wj: true, text: "A new commandment I give to you, that you love one another. Just as I have loved you, you also love one another."} ]
        , 35: [ {class: "vs", wj: true, text: "By this everyone will know that you are my disciples, if you have love for one another.”"} ]
        , 36: [ {class: "", text: "Simon Peter said to him, “Lord, where are you going?”</p><p> Jesus answered,"}
              , {class: "vs", wj: true, text: "“Where I am going, you can’t follow now, but you will follow afterwards.”"}
              ]
        , 37: [ {class: "vs", text: "Peter said to him, “Lord, why can’t I follow you now? I will lay down my life for you.”"} ]
        , 38: [ {class: "", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“Will you lay down your life for me? Most certainly I tell you, the rooster won’t crow until you have denied me three times."}
              ]
        }
  , 14: { 1:  [ {class: "vs", wj: true, text: "“Don’t let your heart be troubled. Believe in God. Believe also in me."} ]
        , 2:  [ {class: "vs", wj: true, text: "In my Father’s house are many homes. If it weren’t so, I would have told you. I am going to prepare a place for you."} ]
        , 3:  [ {class: "vs", wj: true, text: "If I go and prepare a place for you, I will come again, and will receive you to myself; that where I am, you may be there also."} ]
        , 4:  [ {class: "vs", wj: true, text: "You know where I go, and you know the way.”"} ]
        , 5:  [ {class: "vs", text: "Thomas said to him, “Lord, we don’t know where you are going. How can we know the way?”"} ]
        , 6:  [ {class: "endpara", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“I am the way, the truth, and the life. No one comes to the Father, except through me."}
              ]
        , 7:  [ {class: "vs", wj: true, text: "If you had known me, you would have known my Father also. From now on, you know him, and have seen him.”"} ]
        , 8:  [ {class: "vs", text: "Philip said to him, “Lord, show us the Father, and that will be enough for us.”"} ]
        , 9:  [ {class: "endpara", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Have I been with you such a long time, and do you not know me, Philip? He who has seen me has seen the Father. How do you say, ‘Show us the Father?’"}
              ]
        , 10: [ {class: "vs", wj: true, text: "Don’t you believe that I am in the Father, and the Father in me? The words that I tell you, I speak not from myself; but the Father who lives in me does his works."} ]
        , 11: [ {class: "vs", wj: true, text: "Believe me that I am in the Father, and the Father in me; or else believe me for the very works’ sake."} ]
        , 12: [ {class: "vs", wj: true, text: "Most certainly I tell you, he who believes in me, the works that I do, he will do also; and he will do greater works than these, because I am going to my Father."} ]
        , 13: [ {class: "vs", wj: true, text: "Whatever you will ask in my name, I will do it, that the Father may be glorified in the Son."} ]
        , 14: [ {class: "vs", wj: true, text: "If you will ask anything in my name, I will do it."} ]
        , 15: [ {class: "vs", wj: true, text: "If you love me, keep my commandments."} ]
        , 16: [ {class: "vs", wj: true, text: "I will pray to the Father, and he will give you another Counselor,"}
              , {class: "vs", wj: true, text: "that he may be with you forever:"}
              ]
        , 17: [ {class: "vs", wj: true, text: "the Spirit of truth, whom the world can’t receive; for it doesn’t see him and doesn’t know him. You know him, for he lives with you, and will be in you."} ]
        , 18: [ {class: "vs", wj: true, text: "I will not leave you orphans. I will come to you."} ]
        , 19: [ {class: "vs", wj: true, text: "Yet a little while, and the world will see me no more; but you will see me. Because I live, you will live also."} ]
        , 20: [ {class: "vs", wj: true, text: "In that day you will know that I am in my Father, and you in me, and I in you."} ]
        , 21: [ {class: "vs", wj: true, text: "One who has my commandments and keeps them, that person is one who loves me. One who loves me will be loved by my Father, and I will love him, and will reveal myself to him.”"} ]
        , 22: [ {class: "vs", text: "Judas (not Iscariot) said to him, “Lord, what has happened that you are about to reveal yourself to us, and not to the world?”"} ]
        , 23: [ {class: "endpara", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“If a man loves me, he will keep my word. My Father will love him, and we will come to him, and make our home with him."}
              ]
        , 24: [ {class: "vs", wj: true, text: "He who doesn’t love me doesn’t keep my words. The word which you hear isn’t mine, but the Father’s who sent me."} ]
        , 25: [ {class: "vs", wj: true, text: "I have said these things to you while still living with you."} ]
        , 26: [ {class: "vs", wj: true, text: "But the Counselor, the Holy Spirit, whom the Father will send in my name, will teach you all things, and will remind you of all that I said to you."} ]
        , 27: [ {class: "vs", wj: true, text: "Peace I leave with you. My peace I give to you; not as the world gives, I give to you. Don’t let your heart be troubled, neither let it be fearful."} ]
        , 28: [ {class: "vs", wj: true, text: "You heard how I told you, ‘I go away, and I come to you.’ If you loved me, you would have rejoiced, because I said ‘I am going to my Father;’ for the Father is greater than I."} ]
        , 29: [ {class: "vs", wj: true, text: "Now I have told you before it happens so that when it happens, you may believe."} ]
        , 30: [ {class: "vs", wj: true, text: "I will no more speak much with you, for the prince of the world comes, and he has nothing in me."} ]
        , 31: [ {class: "vs", wj: true, text: "But that the world may know that I love the Father, and as the Father commanded me, even so I do. Arise, let’s go from here."} ]
        }
  , 15: { 1:  [ {class: "vs", wj: true, text: "“I am the true vine, and my Father is the farmer."} ]
        , 2:  [ {class: "vs", wj: true, text: "Every branch in me that doesn’t bear fruit, he takes away. Every branch that bears fruit, he prunes, that it may bear more fruit."} ]
        , 3:  [ {class: "vs", wj: true, text: "You are already pruned clean because of the word which I have spoken to you."} ]
        , 4:  [ {class: "vs", wj: true, text: "Remain in me, and I in you. As the branch can’t bear fruit by itself unless it remains in the vine, so neither can you, unless you remain in me."} ]
        , 5:  [ {class: "vs", wj: true, text: "I am the vine. You are the branches. He who remains in me and I in him bears much fruit, for apart from me you can do nothing."} ]
        , 6:  [ {class: "vs", wj: true, text: "If a man doesn’t remain in me, he is thrown out as a branch and is withered; and they gather them, throw them into the fire, and they are burned."} ]
        , 7:  [ {class: "vs", wj: true, text: "If you remain in me, and my words remain in you, you will ask whatever you desire, and it will be done for you."} ]
        , 8:  [ {class: "vs", wj: true, text: "“In this my Father is glorified, that you bear much fruit; and so you will be my disciples."} ]
        , 9:  [ {class: "vs", wj: true, text: "Even as the Father has loved me, I also have loved you. Remain in my love."} ]
        , 10: [ {class: "vs", wj: true, text: "If you keep my commandments, you will remain in my love; even as I have kept my Father’s commandments, and remain in his love."} ]
        , 11: [ {class: "vs", wj: true, text: "I have spoken these things to you, that my joy may remain in you, and that your joy may be made full."} ]
        , 12: [ {class: "vs", wj: true, text: "“This is my commandment, that you love one another, even as I have loved you."} ]
        , 13: [ {class: "vs", wj: true, text: "Greater love has no one than this, that someone lay down his life for his friends."} ]
        , 14: [ {class: "vs", wj: true, text: "You are my friends, if you do whatever I command you."} ]
        , 15: [ {class: "vs", wj: true, text: "No longer do I call you servants, for the servant doesn’t know what his lord does. But I have called you friends, for everything that I heard from my Father, I have made known to you."} ]
        , 16: [ {class: "vs", wj: true, text: "You didn’t choose me, but I chose you and appointed you, that you should go and bear fruit, and that your fruit should remain; that whatever you will ask of the Father in my name, he may give it to you."} ]
        , 17: [ {class: "vs", wj: true, text: "“I command these things to you, that you may love one another."} ]
        , 18: [ {class: "vs", wj: true, text: "If the world hates you, you know that it has hated me before it hated you."} ]
        , 19: [ {class: "vs", wj: true, text: "If you were of the world, the world would love its own. But because you are not of the world, since I chose you out of the world, therefore the world hates you."} ]
        , 20: [ {class: "vs", wj: true, text: "Remember the word that I said to you: ‘A servant is not greater than his lord.’"}
              , {class: "vs", wj: true, text: "If they persecuted me, they will also persecute you. If they kept my word, they will also keep yours."}
              ]
        , 21: [ {class: "vs", wj: true, text: "But they will do all these things to you for my name’s sake, because they don’t know him who sent me."} ]
        , 22: [ {class: "vs", wj: true, text: "If I had not come and spoken to them, they would not have had sin; but now they have no excuse for their sin."} ]
        , 23: [ {class: "vs", wj: true, text: "He who hates me, hates my Father also."} ]
        , 24: [ {class: "vs", wj: true, text: "If I hadn’t done among them the works which no one else did, they wouldn’t have had sin. But now they have seen and also hated both me and my Father."} ]
        , 25: [ {class: "vs", wj: true, text: "But this happened so that the word may be fulfilled which was written in their law, ‘They hated me without a cause.’"} ]
        , 26: [ {class: "vs", wj: true, text: "“When the Counselor"}
              , {class: "vs", wj: true, text: "has come, whom I will send to you from the Father, the Spirit of truth, who proceeds from the Father, he will testify about me."}
              ]
        , 27: [ {class: "vs", wj: true, text: "You will also testify, because you have been with me from the beginning."} ]
        }
  , 16: { 1:  [ {class: "vs", wj: true, text: "“I have said these things to you so that you wouldn’t be caused to stumble."} ]
        , 2:  [ {class: "vs", wj: true, text: "They will put you out of the synagogues. Yes, the time comes that whoever kills you will think that he offers service to God."} ]
        , 3:  [ {class: "vs", wj: true, text: "They will do these things"}
              , {class: "vs", wj: true, text: "because they have not known the Father, nor me."}
              ]
        , 4:  [ {class: "vs", wj: true, text: "But I have told you these things, so that when the time comes, you may remember that I told you about them. I didn’t tell you these things from the beginning, because I was with you."} ]
        , 5:  [ {class: "vs", wj: true, text: "But now I am going to him who sent me, and none of you asks me, ‘Where are you going?’"} ]
        , 6:  [ {class: "vs", wj: true, text: "But because I have told you these things, sorrow has filled your heart."} ]
        , 7:  [ {class: "vs", wj: true, text: "Nevertheless I tell you the truth: It is to your advantage that I go away, for if I don’t go away, the Counselor won’t come to you. But if I go, I will send him to you."} ]
        , 8:  [ {class: "vs", wj: true, text: "When he has come, he will convict the world about sin, about righteousness, and about judgment;"} ]
        , 9:  [ {class: "vs", wj: true, text: "about sin, because they don’t believe in me;"} ]
        , 10: [ {class: "vs", wj: true, text: "about righteousness, because I am going to my Father, and you won’t see me any more;"} ]
        , 11: [ {class: "vs", wj: true, text: "about judgment, because the prince of this world has been judged."} ]
        , 12: [ {class: "vs", wj: true, text: "“I still have many things to tell you, but you can’t bear them now."} ]
        , 13: [ {class: "vs", wj: true, text: "However when he, the Spirit of truth, has come, he will guide you into all truth, for he will not speak from himself; but whatever he hears, he will speak. He will declare to you things that are coming."} ]
        , 14: [ {class: "vs", wj: true, text: "He will glorify me, for he will take from what is mine, and will declare it to you."} ]
        , 15: [ {class: "vs", wj: true, text: "All things that the Father has are mine; therefore I said that he takes"}
              , {class: "vs", wj: true, text: "of mine and will declare it to you."}
              ]
        , 16: [ {class: "vs", wj: true, text: "A little while, and you will not see me. Again a little while, and you will see me.”"} ]
        , 17: [ {class: "endpara", text: "Some of his disciples therefore said to one another, “What is this that he says to us,"}
              , {class: "vs", wj: true, text: "‘A little while, and you won’t see me, and again a little while, and you will see me;’</span> and,"}
              , {class: "vs", wj: true, text: "‘Because I go to the Father’</span> ?”"}
              ]
        , 18: [ {class: "endpara", text: "They said therefore, “What is this that he says,"}
              , {class: "vs", wj: true, text: "‘A little while’</span> ? We don’t know what he is saying.”"}
              ]
        , 19: [ {class: "endpara", text: "Therefore Jesus perceived that they wanted to ask him, and he said to them,"}
              , {class: "vs", wj: true, text: "“Do you inquire among yourselves concerning this, that I said, ‘A little while, and you won’t see me, and again a little while, and you will see me?’"}
              ]
        , 20: [ {class: "vs", wj: true, text: "Most certainly I tell you that you will weep and lament, but the world will rejoice. You will be sorrowful, but your sorrow will be turned into joy."} ]
        , 21: [ {class: "vs", wj: true, text: "A woman, when she gives birth, has sorrow because her time has come. But when she has delivered the child, she doesn’t remember the anguish any more, for the joy that a human being is born into the world."} ]
        , 22: [ {class: "vs", wj: true, text: "Therefore you now have sorrow, but I will see you again, and your heart will rejoice, and no one will take your joy away from you."} ]
        , 23: [ {class: "vs", wj: true, text: "“In that day you will ask me no questions. Most certainly I tell you, whatever you may ask of the Father in my name, he will give it to you."} ]
        , 24: [ {class: "vs", wj: true, text: "Until now, you have asked nothing in my name. Ask, and you will receive, that your joy may be made full."} ]
        , 25: [ {class: "vs", wj: true, text: "I have spoken these things to you in figures of speech. But the time is coming when I will no more speak to you in figures of speech, but will tell you plainly about the Father."} ]
        , 26: [ {class: "vs", wj: true, text: "In that day you will ask in my name; and I don’t say to you that I will pray to the Father for you,"} ]
        , 27: [ {class: "vs", wj: true, text: "for the Father himself loves you, because you have loved me, and have believed that I came from God."} ]
        , 28: [ {class: "vs", wj: true, text: "I came from the Father, and have come into the world. Again, I leave the world, and go to the Father.”"} ]
        , 29: [ {class: "vs", text: "His disciples said to him, “Behold, now you are speaking plainly, and using no figures of speech."} ]
        , 30: [ {class: "vs", text: "Now we know that you know all things, and don’t need for anyone to question you. By this we believe that you came from God.”"} ]
        , 31: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Do you now believe?"}
              ]
        , 32: [ {class: "vs", wj: true, text: "Behold, the time is coming, yes, and has now come, that you will be scattered, everyone to his own place, and you will leave me alone. Yet I am not alone, because the Father is with me."} ]
        , 33: [ {class: "vs", wj: true, text: "I have told you these things, that in me you may have peace. In the world you have trouble; but cheer up! I have overcome the world.”"} ]
        }
  , 17: { 1:  [ {class: "para", text: "Jesus said these things, then lifting up his eyes to heaven, he said,"}
              , {class: "vs", wj: true, text: "“Father, the time has come. Glorify your Son, that your Son may also glorify you;"}
              ]
        , 2:  [ {class: "vs", wj: true, text: "even as you gave him authority over all flesh, so he will give eternal life to all whom you have given him."} ]
        , 3:  [ {class: "vs", wj: true, text: "This is eternal life, that they should know you, the only true God, and him whom you sent, Jesus Christ."} ]
        , 4:  [ {class: "vs", wj: true, text: "I glorified you on the earth. I have accomplished the work which you have given me to do."} ]
        , 5:  [ {class: "vs", wj: true, text: "Now, Father, glorify me with your own self with the glory which I had with you before the world existed."} ]
        , 6:  [ {class: "vs", wj: true, text: "I revealed your name to the people whom you have given me out of the world. They were yours, and you have given them to me. They have kept your word."} ]
        , 7:  [ {class: "vs", wj: true, text: "Now they have known that all things whatever you have given me are from you,"} ]
        , 8:  [ {class: "vs", wj: true, text: "for the words which you have given me I have given to them, and they received them, and knew for sure that I came from you. They have believed that you sent me."} ]
        , 9:  [ {class: "vs", wj: true, text: "I pray for them. I don’t pray for the world, but for those whom you have given me, for they are yours."} ]
        , 10: [ {class: "vs", wj: true, text: "All things that are mine are yours, and yours are mine, and I am glorified in them."} ]
        , 11: [ {class: "vs", wj: true, text: "I am no more in the world, but these are in the world, and I am coming to you. Holy Father, keep them through your name which you have given me, that they may be one, even as we are."} ]
        , 12: [ {class: "vs", wj: true, text: "While I was with them in the world, I kept them in your name. I have kept those whom you have given me. None of them is lost except the son of destruction, that the Scripture might be fulfilled."} ]
        , 13: [ {class: "vs", wj: true, text: "But now I come to you, and I say these things in the world, that they may have my joy made full in themselves."} ]
        , 14: [ {class: "vs", wj: true, text: "I have given them your word. The world hated them, because they are not of the world, even as I am not of the world."} ]
        , 15: [ {class: "vs", wj: true, text: "I pray not that you would take them from the world, but that you would keep them from the evil one."} ]
        , 16: [ {class: "vs", wj: true, text: "They are not of the world even as I am not of the world."} ]
        , 17: [ {class: "vs", wj: true, text: "Sanctify them in your truth. Your word is truth."} ]
        , 18: [ {class: "vs", wj: true, text: "As you sent me into the world, even so I have sent them into the world."} ]
        , 19: [ {class: "vs", wj: true, text: "For their sakes I sanctify myself, that they themselves also may be sanctified in truth."} ]
        , 20: [ {class: "vs", wj: true, text: "Not for these only do I pray, but for those also who will believe in me through their word,"} ]
        , 21: [ {class: "vs", wj: true, text: "that they may all be one; even as you, Father, are in me, and I in you, that they also may be one in us; that the world may believe that you sent me."} ]
        , 22: [ {class: "vs", wj: true, text: "The glory which you have given me, I have given to them; that they may be one, even as we are one;"} ]
        , 23: [ {class: "vs", wj: true, text: "I in them, and you in me, that they may be perfected into one; that the world may know that you sent me and loved them, even as you loved me."} ]
        , 24: [ {class: "vs", wj: true, text: "Father, I desire that they also whom you have given me be with me where I am, that they may see my glory, which you have given me, for you loved me before the foundation of the world."} ]
        , 25: [ {class: "vs", wj: true, text: "Righteous Father, the world hasn’t known you, but I knew you; and these knew that you sent me."} ]
        , 26: [ {class: "vs", wj: true, text: "I made known to them your name, and will make it known; that the love with which you loved me may be in them, and I in them.”"} ]
        }
  , 18: { 1:  [ {class: "vs", text: "When Jesus had spoken these words, he went out with his disciples over the brook Kidron, where there was a garden, into which he and his disciples entered."} ]
        , 2:  [ {class: "vs", text: "Now Judas, who betrayed him, also knew the place, for Jesus often met there with his disciples."} ]
        , 3:  [ {class: "vs", text: "Judas then, having taken a detachment of soldiers and officers from the chief priests and the Pharisees, came there with lanterns, torches, and weapons."} ]
        , 4:  [ {class: "", text: "Jesus therefore, knowing all the things that were happening to him, went out, and said to them,"}
              , {class: "vs", wj: true, text: "“Who are you looking for?”"}
              ]
        , 5:  [ {class: "", text: "They answered him, “Jesus of Nazareth.”</p><p> Jesus said to them,"}
              , {class: "vs", wj: true, text: "“I am he.”</span></p><p> Judas also, who betrayed him, was standing with them."}
              ]
        , 6:  [ {class: "", text: "When therefore he said to them,"}
              , {class: "vs", wj: true, text: "“I am he,”</span> they went backward, and fell to the ground."}
              ]
        , 7:  [ {class: "", text: "Again therefore he asked them,"}
              , {class: "vs", wj: true, text: "“Who are you looking for?”</span></p><p> They said, “Jesus of Nazareth.”"}
              ]
        , 8:  [ {class: "", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“I told you that I am he. If therefore you seek me, let these go their way,”"}
              ]
        , 9:  [ {class: "", text: "that the word might be fulfilled which he spoke,"}
              , {class: "vs", wj: true, text: "“Of those whom you have given me, I have lost none.”"}
              ]
        , 10: [ {class: "vs", text: "Simon Peter therefore, having a sword, drew it, struck the high priest’s servant, and cut off his right ear. The servant’s name was Malchus."} ]
        , 11: [ {class: "", text: "Jesus therefore said to Peter,"}
              , {class: "vs", wj: true, text: "“Put the sword into its sheath. The cup which the Father has given me, shall I not surely drink it?”"}
              ]
        , 12: [ {class: "vs", text: "So the detachment, the commanding officer, and the officers of the Jews seized Jesus and bound him,"} ]
        , 13: [ {class: "vs", text: "and led him to Annas first, for he was father-in-law to Caiaphas, who was high priest that year."} ]
        , 14: [ {class: "vs", text: "Now it was Caiaphas who advised the Jews that it was expedient that one man should perish for the people."} ]
        , 15: [ {class: "vs", text: "Simon Peter followed Jesus, as did another disciple. Now that disciple was known to the high priest, and entered in with Jesus into the court of the high priest;"} ]
        , 16: [ {class: "vs", text: "but Peter was standing at the door outside. So the other disciple, who was known to the high priest, went out and spoke to her who kept the door, and brought in Peter."} ]
        , 17: [ {class: "endpara", text: "Then the maid who kept the door said to Peter, “Are you also one of this man’s disciples?”"}
              , {class: "para", text: "He said, “I am not.”"}
              ]
        , 18: [ {class: "vs", text: "Now the servants and the officers were standing there, having made a fire of coals, for it was cold. They were warming themselves. Peter was with them, standing and warming himself."} ]
        , 19: [ {class: "vs", text: "The high priest therefore asked Jesus about his disciples and about his teaching."} ]
        , 20: [ {class: "", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“I spoke openly to the world. I always taught in synagogues, and in the temple, where the Jews always meet. I said nothing in secret."}
              ]
        , 21: [ {class: "vs", wj: true, text: "Why do you ask me? Ask those who have heard me what I said to them. Behold, they know the things which I said.”"} ]
        , 22: [ {class: "vs", text: "When he had said this, one of the officers standing by slapped Jesus with his hand, saying, “Do you answer the high priest like that?”"} ]
        , 23: [ {class: "", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“If I have spoken evil, testify of the evil; but if well, why do you beat me?”"}
              ]
        , 24: [ {class: "vs", text: "Annas sent him bound to Caiaphas, the high priest."} ]
        , 25: [ {class: "endpara", text: "Now Simon Peter was standing and warming himself. They said therefore to him, “You aren’t also one of his disciples, are you?”"}
              , {class: "para", text: "He denied it and said, “I am not.”"}
              ]
        , 26: [ {class: "vs", text: "One of the servants of the high priest, being a relative of him whose ear Peter had cut off, said, “Didn’t I see you in the garden with him?”"} ]
        , 27: [ {class: "vs", text: "Peter therefore denied it again, and immediately the rooster crowed."} ]
        , 28: [ {class: "vs", text: "They led Jesus therefore from Caiaphas into the Praetorium. It was early, and they themselves didn’t enter into the Praetorium, that they might not be defiled, but might eat the Passover."} ]
        , 29: [ {class: "vs", text: "Pilate therefore went out to them, and said, “What accusation do you bring against this man?”"} ]
        , 30: [ {class: "vs", text: "They answered him, “If this man weren’t an evildoer, we wouldn’t have delivered him up to you.”"} ]
        , 31: [ {class: "endpara", text: "Pilate therefore said to them, “Take him yourselves, and judge him according to your law.”"}
              , {class: "para", text: "Therefore the Jews said to him, “It is illegal for us to put anyone to death,”"}
              ]
        , 32: [ {class: "vs", text: "that the word of Jesus might be fulfilled, which he spoke, signifying by what kind of death he should die."} ]
        , 33: [ {class: "vs", text: "Pilate therefore entered again into the Praetorium, called Jesus, and said to him, “Are you the King of the Jews?”"} ]
        , 34: [ {class: "", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“Do you say this by yourself, or did others tell you about me?”"}
              ]
        , 35: [ {class: "vs", text: "Pilate answered, “I’m not a Jew, am I? Your own nation and the chief priests delivered you to me. What have you done?”"} ]
        , 36: [ {class: "", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“My Kingdom is not of this world. If my Kingdom were of this world, then my servants would fight, that I wouldn’t be delivered to the Jews. But now my Kingdom is not from here.”"}
              ]
        , 37: [ {class: "endpara", text: "Pilate therefore said to him, “Are you a king then?”"}
              , {class: "para", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“You say that I am a king. For this reason I have been born, and for this reason I have come into the world, that I should testify to the truth. Everyone who is of the truth listens to my voice.”"}
              ]
        , 38: [ {class: "endpara", text: "Pilate said to him, “What is truth?”"}
              , {class: "para", text: "When he had said this, he went out again to the Jews, and said to them, “I find no basis for a charge against him."}
              ]
        , 39: [ {class: "vs", text: "But you have a custom, that I should release someone to you at the Passover. Therefore, do you want me to release to you the King of the Jews?”"} ]
        , 40: [ {class: "vs", text: "Then they all shouted again, saying, “Not this man, but Barabbas!” Now Barabbas was a robber."} ]
        }
  , 19: { 1:  [ {class: "vs", text: "So Pilate then took Jesus, and flogged him."} ]
        , 2:  [ {class: "vs", text: "The soldiers twisted thorns into a crown, and put it on his head, and dressed him in a purple garment."} ]
        , 3:  [ {class: "vs", text: "They kept saying, “Hail, King of the Jews!” and they kept slapping him."} ]
        , 4:  [ {class: "vs", text: "Then Pilate went out again, and said to them, “Behold, I bring him out to you, that you may know that I find no basis for a charge against him.”"} ]
        , 5:  [ {class: "vs", text: "Jesus therefore came out, wearing the crown of thorns and the purple garment. Pilate said to them, “Behold, the man!”"} ]
        , 6:  [ {class: "endpara", text: "When therefore the chief priests and the officers saw him, they shouted, saying, “Crucify! Crucify!”"}
              , {class: "para", text: "Pilate said to them, “Take him yourselves, and crucify him, for I find no basis for a charge against him.”"}
              ]
        , 7:  [ {class: "vs", text: "The Jews answered him, “We have a law, and by our law he ought to die, because he made himself the Son of God.”"} ]
        , 8:  [ {class: "vs", text: "When therefore Pilate heard this saying, he was more afraid."} ]
        , 9:  [ {class: "vs", text: "He entered into the Praetorium again, and said to Jesus, “Where are you from?” But Jesus gave him no answer."} ]
        , 10: [ {class: "vs", text: "Pilate therefore said to him, “Aren’t you speaking to me? Don’t you know that I have power to release you and have power to crucify you?”"} ]
        , 11: [ {class: "para", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“You would have no power at all against me, unless it were given to you from above. Therefore he who delivered me to you has greater sin.”"}
              ]
        , 12: [ {class: "vs", text: "At this, Pilate was seeking to release him, but the Jews cried out, saying, “If you release this man, you aren’t Caesar’s friend! Everyone who makes himself a king speaks against Caesar!”"} ]
        , 13: [ {class: "vs", text: "When Pilate therefore heard these words, he brought Jesus out and sat down on the judgment seat at a place called “The Pavement”, but in Hebrew, “Gabbatha.”"} ]
        , 14: [ {class: "vs", text: "Now it was the Preparation Day of the Passover, at about the sixth hour. He said to the Jews, “Behold, your King!”"} ]
        , 15: [ {class: "endpara", text: "They cried out, “Away with him! Away with him! Crucify him!”"}
              , {class: "endpara", text: "Pilate said to them, “Shall I crucify your King?”"}
              , {class: "para", text: "The chief priests answered, “We have no king but Caesar!”"}
              ]
        , 16: [ {class: "vs", text: "So then he delivered him to them to be crucified. So they took Jesus and led him away."} ]
        , 17: [ {class: "vs", text: "He went out, bearing his cross, to the place called “The Place of a Skull”, which is called in Hebrew, “Golgotha”,"} ]
        , 18: [ {class: "vs", text: "where they crucified him, and with him two others, on either side one, and Jesus in the middle."} ]
        , 19: [ {class: "vs", text: "Pilate wrote a title also, and put it on the cross. There was written, “JESUS OF NAZARETH, THE KING OF THE JEWS.”"} ]
        , 20: [ {class: "vs", text: "Therefore many of the Jews read this title, for the place where Jesus was crucified was near the city; and it was written in Hebrew, in Latin, and in Greek."} ]
        , 21: [ {class: "vs", text: "The chief priests of the Jews therefore said to Pilate, “Don’t write, ‘The King of the Jews,’ but, ‘he said, “I am King of the Jews.”’”"} ]
        , 22: [ {class: "vs", text: "Pilate answered, “What I have written, I have written.”"} ]
        , 23: [ {class: "vs", text: "Then the soldiers, when they had crucified Jesus, took his garments and made four parts, to every soldier a part; and also the coat. Now the coat was without seam, woven from the top throughout."} ]
        , 24: [ {class: "para", text: "Then they said to one another, “Let’s not tear it, but cast lots for it to decide whose it will be,” that the Scripture might be fulfilled, which says,"}
              , {class: "q", text: "“They parted my garments among them."}
              , {class: "q2", text: "For my cloak they cast lots.”"}
              , {class: "para", text: "Therefore the soldiers did these things."}
              ]
        , 25: [ {class: "vs", text: "But standing by Jesus’ cross were his mother, his mother’s sister, Mary the wife of Clopas, and Mary Magdalene."} ]
        , 26: [ {class: "", text: "Therefore when Jesus saw his mother, and the disciple whom he loved standing there, he said to his mother,"}
              , {class: "vs", wj: true, text: "“Woman, behold, your son!”"}
              ]
        , 27: [ {class: "", text: "Then he said to the disciple,"}
              , {class: "vs", wj: true, text: "“Behold, your mother!”"}
              , {class: "endpara", text: "From that hour, the disciple took her to his own home."}
              ]
        , 28: [ {class: "endpara", text: "After this, Jesus, seeing that all things were now finished, that the Scripture might be fulfilled, said,"}
              , {class: "vs", wj: true, text: "“I am thirsty.”"}
              ]
        , 29: [ {class: "vs", text: "Now a vessel full of vinegar was set there; so they put a sponge full of the vinegar on hyssop, and held it at his mouth."} ]
        , 30: [ {class: "", text: "When Jesus therefore had received the vinegar, he said,"}
              , {class: "vs", wj: true, text: "“It is finished.”"}
              , {class: "vs", text: "He bowed his head, and gave up his spirit."}
              ]
        , 31: [ {class: "vs", text: "Therefore the Jews, because it was the Preparation Day, so that the bodies wouldn’t remain on the cross on the Sabbath (for that Sabbath was a special one), asked of Pilate that their legs might be broken, and that they might be taken away."} ]
        , 32: [ {class: "vs", text: "Therefore the soldiers came, and broke the legs of the first, and of the other who was crucified with him;"} ]
        , 33: [ {class: "vs", text: "but when they came to Jesus, and saw that he was already dead, they didn’t break his legs."} ]
        , 34: [ {class: "vs", text: "However one of the soldiers pierced his side with a spear, and immediately blood and water came out."} ]
        , 35: [ {class: "vs", text: "He who has seen has testified, and his testimony is true. He knows that he tells the truth, that you may believe."} ]
        , 36: [ {class: "vs", text: "For these things happened that the Scripture might be fulfilled, “A bone of him will not be broken.”"} ]
        , 37: [ {class: "vs", text: "Again another Scripture says, “They will look on him whom they pierced.”"} ]
        , 38: [ {class: "vs", text: "After these things, Joseph of Arimathaea, being a disciple of Jesus, but secretly for fear of the Jews, asked of Pilate that he might take away Jesus’ body. Pilate gave him permission. He came therefore and took away his body."} ]
        , 39: [ {class: "vs", text: "Nicodemus, who at first came to Jesus by night, also came bringing a mixture of myrrh and aloes, about a hundred Roman pounds."} ]
        , 40: [ {class: "vs", text: "So they took Jesus’ body, and bound it in linen cloths with the spices, as the custom of the Jews is to bury."} ]
        , 41: [ {class: "vs", text: "Now in the place where he was crucified there was a garden. In the garden was a new tomb in which no man had ever yet been laid."} ]
        , 42: [ {class: "vs", text: "Then because of the Jews’ Preparation Day (for the tomb was near at hand) they laid Jesus there."} ]
        }
  , 2:  { 1:  [ {class: "vs", text: "The third day, there was a wedding in Cana of Galilee. Jesus’ mother was there."} ]
        , 2:  [ {class: "vs", text: "Jesus also was invited, with his disciples, to the wedding."} ]
        , 3:  [ {class: "vs", text: "When the wine ran out, Jesus’ mother said to him, “They have no wine.”"} ]
        , 4:  [ {class: "para", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Woman, what does that have to do with you and me? My hour has not yet come.”"}
              ]
        , 5:  [ {class: "vs", text: "His mother said to the servants, “Whatever he says to you, do it.”"} ]
        , 6:  [ {class: "vs", text: "Now there were six water pots of stone set there after the Jews’ way of purifying, containing two or three metretes apiece."} ]
        , 7:  [ {class: "para", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“Fill the water pots with water.”"}
              , {class: "endpara", text: "So they filled them up to the brim."}
              ]
        , 8:  [ {class: "para", text: "He said to them,"}
              , {class: "vs", wj: true, text: "“Now draw some out, and take it to the ruler of the feast.”"}
              , {class: "endpara", text: "So they took it."}
              ]
        , 9:  [ {class: "vs", text: "When the ruler of the feast tasted the water now become wine, and didn’t know where it came from (but the servants who had drawn the water knew), the ruler of the feast called the bridegroom"} ]
        , 10: [ {class: "vs", text: "and said to him, “Everyone serves the good wine first, and when the guests have drunk freely, then that which is worse. You have kept the good wine until now!”"} ]
        , 11: [ {class: "vs", text: "This beginning of his signs Jesus did in Cana of Galilee, and revealed his glory; and his disciples believed in him."} ]
        , 12: [ {class: "vs", text: "After this, he went down to Capernaum, he, and his mother, his brothers, and his disciples; and they stayed there a few days."} ]
        , 13: [ {class: "vs", text: "The Passover of the Jews was at hand, and Jesus went up to Jerusalem."} ]
        , 14: [ {class: "vs", text: "He found in the temple those who sold oxen, sheep, and doves, and the changers of money sitting."} ]
        , 15: [ {class: "vs", text: "He made a whip of cords, and threw all out of the temple, both the sheep and the oxen; and he poured out the changers’ money and overthrew their tables."} ]
        , 16: [ {class: "", text: "To those who sold the doves, he said,"}
              , {class: "vs", wj: true, text: "“Take these things out of here! Don’t make my Father’s house a marketplace!”"}
              ]
        , 17: [ {class: "vs", text: "His disciples remembered that it was written, “Zeal for your house will eat me up.”"} ]
        , 18: [ {class: "vs", text: "The Jews therefore answered him, “What sign do you show us, seeing that you do these things?”"} ]
        , 19: [ {class: "", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Destroy this temple, and in three days I will raise it up.”"}
              ]
        , 20: [ {class: "vs", text: "The Jews therefore said, “It took forty-six years to build this temple! Will you raise it up in three days?”"} ]
        , 21: [ {class: "vs", text: "But he spoke of the temple of his body."} ]
        , 22: [ {class: "vs", text: "When therefore he was raised from the dead, his disciples remembered that he said this, and they believed the Scripture, and the word which Jesus had said."} ]
        , 23: [ {class: "vs", text: "Now when he was in Jerusalem at the Passover, during the feast, many believed in his name, observing his signs which he did."} ]
        , 24: [ {class: "vs", text: "But Jesus didn’t entrust himself to them, because he knew everyone,"} ]
        , 25: [ {class: "vs", text: "and because he didn’t need for anyone to testify concerning man; for he himself knew what was in man."} ]
        }
  , 20: { 1:  [ {class: "vs", text: "Now on the first day of the week, Mary Magdalene went early, while it was still dark, to the tomb, and saw the stone taken away from the tomb."} ]
        , 2:  [ {class: "vs", text: "Therefore she ran and came to Simon Peter and to the other disciple whom Jesus loved, and said to them, “They have taken away the Lord out of the tomb, and we don’t know where they have laid him!”"} ]
        , 3:  [ {class: "vs", text: "Therefore Peter and the other disciple went out, and they went toward the tomb."} ]
        , 4:  [ {class: "vs", text: "They both ran together. The other disciple outran Peter, and came to the tomb first."} ]
        , 5:  [ {class: "vs", text: "Stooping and looking in, he saw the linen cloths lying, yet he didn’t enter in."} ]
        , 6:  [ {class: "vs", text: "Then Simon Peter came, following him, and entered into the tomb. He saw the linen cloths lying,"} ]
        , 7:  [ {class: "vs", text: "and the cloth that had been on his head, not lying with the linen cloths, but rolled up in a place by itself."} ]
        , 8:  [ {class: "vs", text: "So then the other disciple who came first to the tomb also entered in, and he saw and believed."} ]
        , 9:  [ {class: "vs", text: "For as yet they didn’t know the Scripture, that he must rise from the dead."} ]
        , 10: [ {class: "vs", text: "So the disciples went away again to their own homes."} ]
        , 11: [ {class: "vs", text: "But Mary was standing outside at the tomb weeping. So as she wept, she stooped and looked into the tomb,"} ]
        , 12: [ {class: "vs", text: "and she saw two angels in white sitting, one at the head, and one at the feet, where the body of Jesus had lain."} ]
        , 13: [ {class: "endpara", text: "They asked her, “Woman, why are you weeping?”"}
              , {class: "para", text: "She said to them, “Because they have taken away my Lord, and I don’t know where they have laid him.”"}
              ]
        , 14: [ {class: "vs", text: "When she had said this, she turned around and saw Jesus standing, and didn’t know that it was Jesus."} ]
        , 15: [ {class: "endpara", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Woman, why are you weeping? Who are you looking for?”"}
              , {class: "para", text: "She, supposing him to be the gardener, said to him, “Sir, if you have carried him away, tell me where you have laid him, and I will take him away.”"}
              ]
        , 16: [ {class: "endpara", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Mary.”"}
              , {class: "para", text: "She turned and said to him, “Rabboni!” which is to say, “Teacher!”"}
              ]
        , 17: [ {class: "endpara", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Don’t hold me, for I haven’t yet ascended to my Father; but go to my brothers and tell them, ‘I am ascending to my Father and your Father, to my God and your God.’”"}
              ]
        , 18: [ {class: "vs", text: "Mary Magdalene came and told the disciples that she had seen the Lord, and that he had said these things to her."} ]
        , 19: [ {class: "endpara", text: "When therefore it was evening on that day, the first day of the week, and when the doors were locked where the disciples were assembled, for fear of the Jews, Jesus came and stood in the middle, and said to them,"}
              , {class: "vs", wj: true, text: "“Peace be to you.”"}
              ]
        , 20: [ {class: "vs", text: "When he had said this, he showed them his hands and his side. The disciples therefore were glad when they saw the Lord."} ]
        , 21: [ {class: "endpara", text: "Jesus therefore said to them again,"}
              , {class: "vs", wj: true, text: "“Peace be to you. As the Father has sent me, even so I send you.”"}
              ]
        , 22: [ {class: "endpara", text: "When he had said this, he breathed on them, and said to them,"}
              , {class: "vs", wj: true, text: "“Receive the Holy Spirit!"}
              ]
        , 23: [ {class: "vs", wj: true, text: "If you forgive anyone’s sins, they have been forgiven them. If you retain anyone’s sins, they have been retained.”"} ]
        , 24: [ {class: "vs", text: "But Thomas, one of the twelve, called Didymus, wasn’t with them when Jesus came."} ]
        , 25: [ {class: "endpara", text: "The other disciples therefore said to him, “We have seen the Lord!”"}
              , {class: "para", text: "But he said to them, “Unless I see in his hands the print of the nails, put my finger into the print of the nails, and put my hand into his side, I will not believe.”"}
              ]
        , 26: [ {class: "endpara", text: "After eight days again his disciples were inside and Thomas was with them. Jesus came, the doors being locked, and stood in the middle, and said,"}
              , {class: "vs", wj: true, text: "“Peace be to you.”"}
              ]
        , 27: [ {class: "", text: "Then he said to Thomas,"}
              , {class: "vs", wj: true, text: "“Reach here your finger, and see my hands. Reach here your hand, and put it into my side. Don’t be unbelieving, but believing.”"}
              ]
        , 28: [ {class: "vs", text: "Thomas answered him, “My Lord and my God!”"} ]
        , 29: [ {class: "endpara", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Because you have seen me,"}
              , {class: "vs", wj: true, text: "you have believed. Blessed are those who have not seen, and have believed.”"}
              ]
        , 30: [ {class: "vs", text: "Therefore Jesus did many other signs in the presence of his disciples, which are not written in this book;"} ]
        , 31: [ {class: "vs", text: "but these are written, that you may believe that Jesus is the Christ, the Son of God, and that believing you may have life in his name."} ]
        }
  , 21: { 1:  [ {class: "vs", text: "After these things, Jesus revealed himself again to the disciples at the sea of Tiberias. He revealed himself this way."} ]
        , 2:  [ {class: "vs", text: "Simon Peter, Thomas called Didymus, Nathanael of Cana in Galilee, and the sons of Zebedee, and two others of his disciples were together."} ]
        , 3:  [ {class: "endpara", text: "Simon Peter said to them, “I’m going fishing.”"}
              , {class: "para", text: "They told him, “We are also coming with you.” They immediately went out, and entered into the boat. That night, they caught nothing."}
              ]
        , 4:  [ {class: "vs", text: "But when day had already come, Jesus stood on the beach, yet the disciples didn’t know that it was Jesus."} ]
        , 5:  [ {class: "endpara", text: "Jesus therefore said to them,"}
              , {class: "vs", wj: true, text: "“Children, have you anything to eat?”</span></p><p> They answered him, “No.”"}
              ]
        , 6:  [ {class: "endpara", text: "He said to them,"}
              , {class: "vs", wj: true, text: "“Cast the net on the right side of the boat, and you will find some.”</span></p><p> They cast it therefore, and now they weren’t able to draw it in for the multitude of fish."}
              ]
        , 7 : [ {class: "endpara", text: "That disciple therefore whom Jesus loved said to Peter, “It’s the Lord!”"}
              , {class: "para", text: "So when Simon Peter heard that it was the Lord, he wrapped his coat around himself (for he was naked), and threw himself into the sea."}
              ]
        , 8:  [ {class: "vs", text: "But the other disciples came in the little boat (for they were not far from the land, but about two hundred cubits away), dragging the net full of fish."} ]
        , 9:  [ {class: "vs", text: "So when they got out on the land, they saw a fire of coals there, with fish and bread laid on it."} ]
        , 10: [ {class: "endpara", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“Bring some of the fish which you have just caught.”"}
              ]
        , 11: [ {class: "vs", text: "Simon Peter went up, and drew the net to land, full of one hundred fifty-three great fish. Even though there were so many, the net wasn’t torn."} ]
        , 12: [ {class: "endpara", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“Come and eat breakfast!”"}
              , {class: "para", text: "None of the disciples dared inquire of him, “Who are you?” knowing that it was the Lord."}
              ]
        , 13: [ {class: "vs", text: "Then Jesus came and took the bread, gave it to them, and the fish likewise."} ]
        , 14: [ {class: "vs", text: "This is now the third time that Jesus was revealed to his disciples after he had risen from the dead."} ]
        , 15: [ {class: "endpara", text: "So when they had eaten their breakfast, Jesus said to Simon Peter,"}
              , {class: "vs", wj: true, text: "“Simon, son of Jonah, do you love me more than these?”"}
              , {class: "para", text: "He said to him, “Yes, Lord; you know that I have affection for you.”"}
              , {class: "para", text: "He said to him,"}
              , {class: "vs", wj: true, text: "“Feed my lambs.”"}
              ]
        , 16: [ {class: "endpara", text: "He said to him again a second time,"}
              , {class: "vs", wj: true, text: "“Simon, son of Jonah, do you love me?”"}
              , {class: "para", text: "He said to him, “Yes, Lord; you know that I have affection for you.”"}
              , {class: "para", text: "He said to him,"}
              , {class: "vs", wj: true, text: "“Tend my sheep.”"}
              ]
        , 17: [ {class: "para", text: "He said to him the third time,"}
              , {class: "vs", wj: true, text: "“Simon, son of Jonah, do you have affection for me?”"}
              , {class: "endpara", text: "Peter was grieved because he asked him the third time,"}
              , {class: "vs", wj: true, text: "“Do you have affection for me?”</span> He said to him, “Lord, you know everything. You know that I have affection for you.”</p><p> Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Feed my sheep."}
              ]
        , 18: [ {class: "vs", wj: true, text: "Most certainly I tell you, when you were young, you dressed yourself and walked where you wanted to. But when you are old, you will stretch out your hands, and another will dress you and carry you where you don’t want to go.”"} ]
        , 19: [ {class: "endpara", text: "Now he said this, signifying by what kind of death he would glorify God. When he had said this, he said to him,"}
              , {class: "vs", wj: true, text: "“Follow me.”"}
              ]
        , 20: [ {class: "vs", text: "Then Peter, turning around, saw a disciple following. This was the disciple whom Jesus loved, the one who had also leaned on Jesus’ breast at the supper and asked, “Lord, who is going to betray you?”"} ]
        , 21: [ {class: "vs", text: "Peter seeing him, said to Jesus, “Lord, what about this man?”"} ]
        , 22: [ {class: "endpara", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“If I desire that he stay until I come, what is that to you? You follow me.”"}
              ]
        , 23: [ {class: "endpara", text: "This saying therefore went out among the brothers, that this disciple wouldn’t die. Yet Jesus didn’t say to him that he wouldn’t die, but,"}
              , {class: "vs", wj: true, text: "“If I desire that he stay until I come, what is that to you?”"}
              ]
        , 24: [ {class: "vs", text: "This is the disciple who testifies about these things, and wrote these things. We know that his witness is true."} ]
        , 25: [ {class: "vs", text: "There are also many other things which Jesus did, which if they would all be written, I suppose that even the world itself wouldn’t have room for the books that would be written."} ]
        }
  , 3:  { 1:  [ {class: "vs", text: "Now there was a man of the Pharisees named Nicodemus, a ruler of the Jews."} ]
        , 2:  [ {class: "vs", text: "The same came to him by night, and said to him, “Rabbi, we know that you are a teacher come from God, for no one can do these signs that you do, unless God is with him.”"} ]
        , 3:  [ {class: "endpara", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“Most certainly, I tell you, unless one is born anew,"}
              , {class: "vs", wj: true, text: "he can’t see God’s Kingdom.”"}
              ]
        , 4:  [ {class: "vs", text: "Nicodemus said to him, “How can a man be born when he is old? Can he enter a second time into his mother’s womb, and be born?”"} ]
        , 5:  [ {class: "endpara", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“Most certainly I tell you, unless one is born of water and spirit, he can’t enter into God’s Kingdom."}
              ]
        , 6:  [ {class: "vs", wj: true, text: "That which is born of the flesh is flesh. That which is born of the Spirit is spirit."} ]
        , 7:  [ {class: "vs", wj: true, text: "Don’t marvel that I said to you, ‘You must be born anew.’"} ]
        , 8:  [ {class: "vs", wj: true, text: "The wind"}
              , {class: "vs", wj: true, text: "blows where it wants to, and you hear its sound, but don’t know where it comes from and where it is going. So is everyone who is born of the Spirit.”"}
              ]
        , 9:  [ {class: "vs", text: "Nicodemus answered him, “How can these things be?”"} ]
        , 10: [ {class: "", text: "Jesus answered him,"}
              , {class: "vs", wj: true, text: "“Are you the teacher of Israel, and don’t understand these things?"}
              ]
        , 11: [ {class: "vs", wj: true, text: "Most certainly I tell you, we speak that which we know, and testify of that which we have seen, and you don’t receive our witness."} ]
        , 12: [ {class: "vs", wj: true, text: "If I told you earthly things and you don’t believe, how will you believe if I tell you heavenly things?"} ]
        , 13: [ {class: "vs", wj: true, text: "No one has ascended into heaven but he who descended out of heaven, the Son of Man, who is in heaven."} ]
        , 14: [ {class: "vs", wj: true, text: "As Moses lifted up the serpent in the wilderness, even so must the Son of Man be lifted up,"} ]
        , 15: [ {class: "vs", wj: true, text: "that whoever believes in him should not perish, but have eternal life."} ]
        , 16: [ {class: "vs", wj: true, text: "For God so loved the world, that he gave his one and only Son, that whoever believes in him should not perish, but have eternal life."} ]
        , 17: [ {class: "vs", wj: true, text: "For God didn’t send his Son into the world to judge the world, but that the world should be saved through him."} ]
        , 18: [ {class: "vs", wj: true, text: "He who believes in him is not judged. He who doesn’t believe has been judged already, because he has not believed in the name of the one and only Son of God."} ]
        , 19: [ {class: "vs", wj: true, text: "This is the judgment, that the light has come into the world, and men loved the darkness rather than the light; for their works were evil."} ]
        , 20: [ {class: "vs", wj: true, text: "For everyone who does evil hates the light, and doesn’t come to the light, lest his works would be exposed."} ]
        , 21: [ {class: "vs", wj: true, text: "But he who does the truth comes to the light, that his works may be revealed, that they have been done in God.”"} ]
        , 22: [ {class: "vs", text: "After these things, Jesus came with his disciples into the land of Judea. He stayed there with them and baptized."} ]
        , 23: [ {class: "vs", text: "John also was baptizing in Enon near Salim, because there was much water there. They came, and were baptized;"} ]
        , 24: [ {class: "vs", text: "for John was not yet thrown into prison."} ]
        , 25: [ {class: "vs", text: "Therefore a dispute arose on the part of John’s disciples with some Jews about purification."} ]
        , 26: [ {class: "vs", text: "They came to John and said to him, “Rabbi, he who was with you beyond the Jordan, to whom you have testified, behold, he baptizes, and everyone is coming to him.”"} ]
        , 27: [ {class: "vs", text: "John answered, “A man can receive nothing unless it has been given him from heaven."} ]
        , 28: [ {class: "vs", text: "You yourselves testify that I said, ‘I am not the Christ,’ but, ‘I have been sent before him.’"} ]
        , 29: [ {class: "vs", text: "He who has the bride is the bridegroom; but the friend of the bridegroom, who stands and hears him, rejoices greatly because of the bridegroom’s voice. This, my joy, therefore is made full."} ]
        , 30: [ {class: "vs", text: "He must increase, but I must decrease."} ]
        , 31: [ {class: "vs", text: "He who comes from above is above all. He who is from the earth belongs to the earth and speaks of the earth. He who comes from heaven is above all."} ]
        , 32: [ {class: "vs", text: "What he has seen and heard, of that he testifies; and no one receives his witness."} ]
        , 33: [ {class: "vs", text: "He who has received his witness has set his seal to this, that God is true."} ]
        , 34: [ {class: "vs", text: "For he whom God has sent speaks the words of God; for God gives the Spirit without measure."} ]
        , 35: [ {class: "vs", text: "The Father loves the Son, and has given all things into his hand."} ]
        , 36: [ {class: "vs", text: "One who believes in the Son has eternal life, but one who disobeys the Son won’t see life, but the wrath of God remains on him.”"} ]
        }
  , 4:  { 1:  [ {class: "vs", text: "Therefore when the Lord knew that the Pharisees had heard that Jesus was making and baptizing more disciples than John"} ]
        , 2:  [ {class: "vs", text: "(although Jesus himself didn’t baptize, but his disciples),"} ]
        , 3:  [ {class: "vs", text: "he left Judea and departed into Galilee."} ]
        , 4:  [ {class: "vs", text: "He needed to pass through Samaria."} ]
        , 5:  [ {class: "vs", text: "So he came to a city of Samaria, called Sychar, near the parcel of ground that Jacob gave to his son, Joseph."} ]
        , 6:  [ {class: "vs", text: "Jacob’s well was there. Jesus therefore, being tired from his journey, sat down by the well. It was about the sixth hour."} ]
        , 7:  [ {class: "endpara", text: "A woman of Samaria came to draw water. Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Give me a drink.”"}
              ]
        , 8:  [ {class: "vs", text: "For his disciples had gone away into the city to buy food."} ]
        , 9:  [ {class: "vs", text: "The Samaritan woman therefore said to him, “How is it that you, being a Jew, ask for a drink from me, a Samaritan woman?” (For Jews have no dealings with Samaritans.)"} ]
        , 10: [ {class: "endpara", text: "Jesus answered her,"}
              , {class: "vs", wj: true, text: "“If you knew the gift of God, and who it is who says to you, ‘Give me a drink,’ you would have asked him, and he would have given you living water.”"}
              ]
        , 11: [ {class: "vs", text: "The woman said to him, “Sir, you have nothing to draw with, and the well is deep. So where do you get that living water?"} ]
        , 12: [ {class: "vs", text: "Are you greater than our father, Jacob, who gave us the well and drank from it himself, as did his children and his livestock?”"} ]
        , 13: [ {class: "endpara", text: "Jesus answered her,"}
              , {class: "vs", wj: true, text: "“Everyone who drinks of this water will thirst again,"}
              ]
        , 14: [ {class: "vs", wj: true, text: "but whoever drinks of the water that I will give him will never thirst again; but the water that I will give him will become in him a well of water springing up to eternal life.”"} ]
        , 15: [ {class: "vs", text: "The woman said to him, “Sir, give me this water, so that I don’t get thirsty, neither come all the way here to draw.”"} ]
        , 16: [ {class: "endpara", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Go, call your husband, and come here.”"}
              ]
        , 17: [ {class: "endpara", text: "The woman answered, “I have no husband.”"}
              , {class: "para", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“You said well, ‘I have no husband,’"}
              ]
        , 18: [ {class: "vs", wj: true, text: "for you have had five husbands; and he whom you now have is not your husband. This you have said truly.”"} ]
        , 19: [ {class: "vs", text: "The woman said to him, “Sir, I perceive that you are a prophet."} ]
        , 20: [ {class: "vs", text: "Our fathers worshiped in this mountain, and you Jews say that in Jerusalem is the place where people ought to worship.”"} ]
        , 21: [ {class: "endpara", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“Woman, believe me, the hour comes, when neither in this mountain, nor in Jerusalem, will you worship the Father."}
              ]
        , 22: [ {class: "vs", wj: true, text: "You worship that which you don’t know. We worship that which we know; for salvation is from the Jews."} ]
        , 23: [ {class: "vs", wj: true, text: "But the hour comes, and now is, when the true worshipers will worship the Father in spirit and truth, for the Father seeks such to be his worshipers."} ]
        , 24: [ {class: "vs", wj: true, text: "God is spirit, and those who worship him must worship in spirit and truth.”"} ]
        , 25: [ {class: "vs", text: "The woman said to him, “I know that Messiah comes, he who is called Christ. When he has come, he will declare to us all things.”"} ]
        , 26: [ {class: "endpara", text: "Jesus said to her,"}
              , {class: "vs", wj: true, text: "“I am he, the one who speaks to you.”"}
              ]
        , 27: [ {class: "vs", text: "At this, his disciples came. They marveled that he was speaking with a woman; yet no one said, “What are you looking for?” or, “Why do you speak with her?”"} ]
        , 28: [ {class: "vs", text: "So the woman left her water pot, went away into the city, and said to the people,"} ]
        , 29: [ {class: "vs", text: "“Come, see a man who told me everything that I did. Can this be the Christ?”"} ]
        , 30: [ {class: "vs", text: "They went out of the city, and were coming to him."} ]
        , 31: [ {class: "vs", text: "In the meanwhile, the disciples urged him, saying, “Rabbi, eat.”"} ]
        , 32: [ {class: "endpara", text: "But he said to them,"}
              , {class: "vs", wj: true, text: "“I have food to eat that you don’t know about.”"}
              ]
        , 33: [ {class: "vs", text: "The disciples therefore said to one another, “Has anyone brought him something to eat?”"} ]
        , 34: [ {class: "", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“My food is to do the will of him who sent me and to accomplish his work."}
              ]
        , 35: [ {class: "vs", wj: true, text: "Don’t you say, ‘There are yet four months until the harvest?’ Behold, I tell you, lift up your eyes and look at the fields, that they are white for harvest already."} ]
        , 36: [ {class: "vs", wj: true, text: "He who reaps receives wages and gathers fruit to eternal life; that both he who sows and he who reaps may rejoice together."} ]
        , 37: [ {class: "vs", wj: true, text: "For in this the saying is true, ‘One sows, and another reaps.’"} ]
        , 38: [ {class: "vs", wj: true, text: "I sent you to reap that for which you haven’t labored. Others have labored, and you have entered into their labor.”"} ]
        , 39: [ {class: "vs", text: "From that city many of the Samaritans believed in him because of the word of the woman, who testified, “He told me everything that I did.”"} ]
        , 40: [ {class: "vs", text: "So when the Samaritans came to him, they begged him to stay with them. He stayed there two days."} ]
        , 41: [ {class: "vs", text: "Many more believed because of his word."} ]
        , 42: [ {class: "vs", text: "They said to the woman, “Now we believe, not because of your speaking; for we have heard for ourselves, and know that this is indeed the Christ, the Savior of the world.”"} ]
        , 43: [ {class: "vs", text: "After the two days he went out from there and went into Galilee."} ]
        , 44: [ {class: "vs", text: "For Jesus himself testified that a prophet has no honor in his own country."} ]
        , 45: [ {class: "vs", text: "So when he came into Galilee, the Galileans received him, having seen all the things that he did in Jerusalem at the feast, for they also went to the feast."} ]
        , 46: [ {class: "vs", text: "Jesus came therefore again to Cana of Galilee, where he made the water into wine. There was a certain nobleman whose son was sick at Capernaum."} ]
        , 47: [ {class: "vs", text: "When he heard that Jesus had come out of Judea into Galilee, he went to him, and begged him that he would come down and heal his son, for he was at the point of death."} ]
        , 48: [ {class: "endpara", text: "Jesus therefore said to him,"}
              , {class: "vs", wj: true, text: "“Unless you see signs and wonders, you will in no way believe.”"}
              ]
        , 49: [ {class: "vs", text: "The nobleman said to him, “Sir, come down before my child dies.”"} ]
        , 50: [ {class: "endpara", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Go your way. Your son lives.”</span> The man believed the word that Jesus spoke to him, and he went his way."}
              ]
        , 51: [ {class: "vs", text: "As he was now going down, his servants met him and reported, saying “Your child lives!”"} ]
        , 52: [ {class: "vs", text: "So he inquired of them the hour when he began to get better. They said therefore to him, “Yesterday at the seventh hour, the fever left him.”"} ]
        , 53: [ {class: "endpara", text: "So the father knew that it was at that hour in which Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Your son lives.”</span> He believed, as did his whole house."}
              ]
        , 54: [ {class: "vs", text: "This is again the second sign that Jesus did, having come out of Judea into Galilee."} ]
        }
  , 5:  { 1:  [ {class: "vs", text: "After these things, there was a feast of the Jews, and Jesus went up to Jerusalem."} ]
        , 2:  [ {class: "vs", text: "Now in Jerusalem by the sheep gate, there is a pool, which is called in Hebrew, “Bethesda”, having five porches."} ]
        , 3:  [ {class: "vs", text: "In these lay a great multitude of those who were sick, blind, lame, or paralyzed, waiting for the moving of the water;"} ]
        , 4:  [ {class: "vs", text: "for an angel went down at certain times into the pool and stirred up the water. Whoever stepped in first after the stirring of the water was healed of whatever disease he had."} ]
        , 5:  [ {class: "vs", text: "A certain man was there who had been sick for thirty-eight years."} ]
        , 6:  [ {class: "endpara", text: "When Jesus saw him lying there, and knew that he had been sick for a long time, he asked him,"}
              , {class: "vs", wj: true, text: "“Do you want to be made well?”"}
              ]
        , 7:  [ {class: "vs", text: "The sick man answered him, “Sir, I have no one to put me into the pool when the water is stirred up, but while I’m coming, another steps down before me.”"} ]
        , 8:  [ {class: "endpara", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“Arise, take up your mat, and walk.”"}
              ]
        , 9:  [ {class: "endpara", text: "Immediately, the man was made well, and took up his mat and walked."}
              , {class: "para", text: "Now it was the Sabbath on that day."}
              ]
        , 10: [ {class: "vs", text: "So the Jews said to him who was cured, “It is the Sabbath. It is not lawful for you to carry the mat.”"} ]
        , 11: [ {class: "endpara", text: "He answered them, “He who made me well said to me,"}
              , {class: "vs", wj: true, text: "‘Take up your mat and walk.’</span> ”"}
              ]
        , 12: [ {class: "endpara", text: "Then they asked him, “Who is the man who said to you,"}
              , {class: "vs", wj: true, text: "‘Take up your mat and walk’</span> ?”"}
              ]
        , 13: [ {class: "vs", text: "But he who was healed didn’t know who it was, for Jesus had withdrawn, a crowd being in the place."} ]
        , 14: [ {class: "endpara", text: "Afterward Jesus found him in the temple, and said to him,"}
              , {class: "vs", wj: true, text: "“Behold, you are made well. Sin no more, so that nothing worse happens to you.”"}
              ]
        , 15: [ {class: "vs", text: "The man went away, and told the Jews that it was Jesus who had made him well."} ]
        , 16: [ {class: "vs", text: "For this cause the Jews persecuted Jesus, and sought to kill him, because he did these things on the Sabbath."} ]
        , 17: [ {class: "endpara", text: "But Jesus answered them,"}
              , {class: "vs", wj: true, text: "“My Father is still working, so I am working, too.”"}
              ]
        , 18: [ {class: "vs", text: "For this cause therefore the Jews sought all the more to kill him, because he not only broke the Sabbath, but also called God his own Father, making himself equal with God."} ]
        , 19: [ {class: "endpara", text: "Jesus therefore answered them,"}
              , {class: "vs", wj: true, text: "“Most certainly, I tell you, the Son can do nothing of himself, but what he sees the Father doing. For whatever things he does, these the Son also does likewise."}
              ]
        , 20: [ {class: "vs", wj: true, text: "For the Father has affection for the Son, and shows him all things that he himself does. He will show him greater works than these, that you may marvel."} ]
        , 21: [ {class: "vs", wj: true, text: "For as the Father raises the dead and gives them life, even so the Son also gives life to whom he desires."} ]
        , 22: [ {class: "vs", wj: true, text: "For the Father judges no one, but he has given all judgment to the Son,"} ]
        , 23: [ {class: "vs", wj: true, text: "that all may honor the Son, even as they honor the Father. He who doesn’t honor the Son doesn’t honor the Father who sent him."} ]
        , 24: [ {class: "vs", wj: true, text: "“Most certainly I tell you, he who hears my word and believes him who sent me has eternal life, and doesn’t come into judgment, but has passed out of death into life."} ]
        , 25: [ {class: "vs", wj: true, text: "Most certainly I tell you, the hour comes, and now is, when the dead will hear the Son of God’s voice; and those who hear will live."} ]
        , 26: [ {class: "vs", wj: true, text: "For as the Father has life in himself, even so he gave to the Son also to have life in himself."} ]
        , 27: [ {class: "vs", wj: true, text: "He also gave him authority to execute judgment, because he is a son of man."} ]
        , 28: [ {class: "vs", wj: true, text: "Don’t marvel at this, for the hour comes in which all who are in the tombs will hear his voice,"} ]
        , 29: [ {class: "vs", wj: true, text: "and will come out; those who have done good, to the resurrection of life; and those who have done evil, to the resurrection of judgment."} ]
        , 30: [ {class: "vs", wj: true, text: "I can of myself do nothing. As I hear, I judge, and my judgment is righteous; because I don’t seek my own will, but the will of my Father who sent me."} ]
        , 31: [ {class: "vs", wj: true, text: "“If I testify about myself, my witness is not valid."} ]
        , 32: [ {class: "vs", wj: true, text: "It is another who testifies about me. I know that the testimony which he testifies about me is true."} ]
        , 33: [ {class: "vs", wj: true, text: "You have sent to John, and he has testified to the truth."} ]
        , 34: [ {class: "vs", wj: true, text: "But the testimony which I receive is not from man. However, I say these things that you may be saved."} ]
        , 35: [ {class: "vs", wj: true, text: "He was the burning and shining lamp, and you were willing to rejoice for a while in his light."} ]
        , 36: [ {class: "vs", wj: true, text: "But the testimony which I have is greater than that of John, for the works which the Father gave me to accomplish, the very works that I do, testify about me, that the Father has sent me."} ]
        , 37: [ {class: "vs", wj: true, text: "The Father himself, who sent me, has testified about me. You have neither heard his voice at any time, nor seen his form."} ]
        , 38: [ {class: "vs", wj: true, text: "You don’t have his word living in you, because you don’t believe him whom he sent."} ]
        , 39: [ {class: "vs", wj: true, text: "“You search the Scriptures, because you think that in them you have eternal life; and these are they which testify about me."} ]
        , 40: [ {class: "vs", wj: true, text: "Yet you will not come to me, that you may have life."} ]
        , 41: [ {class: "vs", wj: true, text: "I don’t receive glory from men."} ]
        , 42: [ {class: "vs", wj: true, text: "But I know you, that you don’t have God’s love in yourselves."} ]
        , 43: [ {class: "vs", wj: true, text: "I have come in my Father’s name, and you don’t receive me. If another comes in his own name, you will receive him."} ]
        , 44: [ {class: "vs", wj: true, text: "How can you believe, who receive glory from one another, and you don’t seek the glory that comes from the only God?"} ]
        , 45: [ {class: "vs", wj: true, text: "“Don’t think that I will accuse you to the Father. There is one who accuses you, even Moses, on whom you have set your hope."} ]
        , 46: [ {class: "vs", wj: true, text: "For if you believed Moses, you would believe me; for he wrote about me."} ]
        , 47: [ {class: "vs", wj: true, text: "But if you don’t believe his writings, how will you believe my words?”"} ]
        }
  , 6:  { 1:  [ {class: "vs", text: "After these things, Jesus went away to the other side of the sea of Galilee, which is also called the Sea of Tiberias."} ]
        , 2:  [ {class: "vs", text: "A great multitude followed him, because they saw his signs which he did on those who were sick."} ]
        , 3:  [ {class: "vs", text: "Jesus went up into the mountain, and he sat there with his disciples."} ]
        , 4:  [ {class: "vs", text: "Now the Passover, the feast of the Jews, was at hand."} ]
        , 5:  [ {class: "endpara", text: "Jesus therefore lifting up his eyes, and seeing that a great multitude was coming to him, said to Philip,"}
              , {class: "vs", wj: true, text: "“Where are we to buy bread, that these may eat?”"}
              ]
        , 6:  [ {class: "vs", text: "He said this to test him, for he himself knew what he would do."} ]
        , 7:  [ {class: "vs", text: "Philip answered him, “Two hundred denarii worth of bread is not sufficient for them, that every one of them may receive a little.”"} ]
        , 8:  [ {class: "vs", text: "One of his disciples, Andrew, Simon Peter’s brother, said to him,"} ]
        , 9:  [ {class: "vs", text: "“There is a boy here who has five barley loaves and two fish, but what are these among so many?”"} ]
        , 10: [ {class: "endpara", text: "Jesus said,"}
              , {class: "vs", wj: true, text: "“Have the people sit down.”</span> Now there was much grass in that place. So the men sat down, in number about five thousand."}
              ]
        , 11: [ {class: "vs", text: "Jesus took the loaves; and having given thanks, he distributed to the disciples, and the disciples to those who were sitting down; likewise also of the fish as much as they desired."} ]
        , 12: [ {class: "endpara", text: "When they were filled, he said to his disciples,"}
              , {class: "vs", wj: true, text: "“Gather up the broken pieces which are left over, that nothing be lost.”"}
              ]
        , 13: [ {class: "vs", text: "So they gathered them up, and filled twelve baskets with broken pieces from the five barley loaves, which were left over by those who had eaten."} ]
        , 14: [ {class: "vs", text: "When therefore the people saw the sign which Jesus did, they said, “This is truly the prophet who comes into the world.”"} ]
        , 15: [ {class: "vs", text: "Jesus therefore, perceiving that they were about to come and take him by force to make him king, withdrew again to the mountain by himself."} ]
        , 16: [ {class: "vs", text: "When evening came, his disciples went down to the sea."} ]
        , 17: [ {class: "vs", text: "They entered into the boat, and were going over the sea to Capernaum. It was now dark, and Jesus had not come to them."} ]
        , 18: [ {class: "vs", text: "The sea was tossed by a great wind blowing."} ]
        , 19: [ {class: "vs", text: "When therefore they had rowed about twenty-five or thirty stadia, they saw Jesus walking on the sea, and drawing near to the boat; and they were afraid."} ]
        , 20: [ {class: "endpara", text: "But he said to them,"}
              , {class: "vs", wj: true, text: "“It is I."}
              , {class: "vs", wj: true, text: "Don’t be afraid.”"}
              ]
        , 21: [ {class: "vs", text: "They were willing therefore to receive him into the boat. Immediately the boat was at the land where they were going."} ]
        , 22: [ {class: "vs", text: "On the next day, the multitude that stood on the other side of the sea saw that there was no other boat there, except the one in which his disciples had embarked, and that Jesus hadn’t entered with his disciples into the boat, but his disciples had gone away alone."} ]
        , 23: [ {class: "vs", text: "However boats from Tiberias came near to the place where they ate the bread after the Lord had given thanks."} ]
        , 24: [ {class: "vs", text: "When the multitude therefore saw that Jesus wasn’t there, nor his disciples, they themselves got into the boats, and came to Capernaum, seeking Jesus."} ]
        , 25: [ {class: "vs", text: "When they found him on the other side of the sea, they asked him, “Rabbi, when did you come here?”"} ]
        , 26: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Most certainly I tell you, you seek me, not because you saw signs, but because you ate of the loaves, and were filled."}
              ]
        , 27: [ {class: "vs", wj: true, text: "Don’t work for the food which perishes, but for the food which remains to eternal life, which the Son of Man will give to you. For God the Father has sealed him.”"} ]
        , 28: [ {class: "vs", text: "They said therefore to him, “What must we do, that we may work the works of God?”"} ]
        , 29: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“This is the work of God, that you believe in him whom he has sent.”"}
              ]
        , 30: [ {class: "vs", text: "They said therefore to him, “What then do you do for a sign, that we may see and believe you? What work do you do?"} ]
        , 31: [ {class: "vs", text: "Our fathers ate the manna in the wilderness. As it is written, ‘He gave them bread out of heaven to eat.’”"} ]
        , 32: [ {class: "endpara", text: "Jesus therefore said to them,"}
              , {class: "vs", wj: true, text: "“Most certainly, I tell you, it wasn’t Moses who gave you the bread out of heaven, but my Father gives you the true bread out of heaven."}
              ]
        , 33: [ {class: "vs", wj: true, text: "For the bread of God is that which comes down out of heaven, and gives life to the world.”"} ]
        , 34: [ {class: "vs", text: "They said therefore to him, “Lord, always give us this bread.”"} ]
        , 35: [ {class: "endpara", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“I am the bread of life. Whoever comes to me will not be hungry, and whoever believes in me will never be thirsty."}
              ]
        , 36: [ {class: "vs", wj: true, text: "But I told you that you have seen me, and yet you don’t believe."} ]
        , 37: [ {class: "vs", wj: true, text: "All those whom the Father gives me will come to me. He who comes to me I will in no way throw out."} ]
        , 38: [ {class: "vs", wj: true, text: "For I have come down from heaven, not to do my own will, but the will of him who sent me."} ]
        , 39: [ {class: "vs", wj: true, text: "This is the will of my Father who sent me, that of all he has given to me I should lose nothing, but should raise him up at the last day."} ]
        , 40: [ {class: "vs", wj: true, text: "This is the will of the one who sent me, that everyone who sees the Son, and believes in him, should have eternal life; and I will raise him up at the last day.”"} ]
        , 41: [ {class: "endpara", text: "The Jews therefore murmured concerning him, because he said,"}
              , {class: "vs", wj: true, text: "“I am the bread which came down out of heaven.”"}
              ]
        , 42: [ {class: "endpara", text: "They said, “Isn’t this Jesus, the son of Joseph, whose father and mother we know? How then does he say,"}
              , {class: "vs", wj: true, text: "‘I have come down out of heaven?’”"}
              ]
        , 43: [ {class: "endpara", text: "Therefore Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Don’t murmur among yourselves."}
              ]
        , 44: [ {class: "vs", wj: true, text: "No one can come to me unless the Father who sent me draws him, and I will raise him up in the last day."} ]
        , 45: [ {class: "vs", wj: true, text: "It is written in the prophets, ‘They will all be taught by God.’"}
              , {class: "vs", wj: true, text: "Therefore everyone who hears from the Father and has learned, comes to me."}
              ]
        , 46: [ {class: "vs", wj: true, text: "Not that anyone has seen the Father, except he who is from God. He has seen the Father."} ]
        , 47: [ {class: "vs", wj: true, text: "Most certainly, I tell you, he who believes in me has eternal life."} ]
        , 48: [ {class: "vs", wj: true, text: "I am the bread of life."} ]
        , 49: [ {class: "vs", wj: true, text: "Your fathers ate the manna in the wilderness and they died."} ]
        , 50: [ {class: "vs", wj: true, text: "This is the bread which comes down out of heaven, that anyone may eat of it and not die."} ]
        , 51: [ {class: "vs", wj: true, text: "I am the living bread which came down out of heaven. If anyone eats of this bread, he will live forever. Yes, the bread which I will give for the life of the world is my flesh.”"} ]
        , 52: [ {class: "vs", text: "The Jews therefore contended with one another, saying, “How can this man give us his flesh to eat?”"} ]
        , 53: [ {class: "endpara", text: "Jesus therefore said to them,"}
              , {class: "vs", wj: true, text: "“Most certainly I tell you, unless you eat the flesh of the Son of Man and drink his blood, you don’t have life in yourselves."}
              ]
        , 54: [ {class: "vs", wj: true, text: "He who eats my flesh and drinks my blood has eternal life, and I will raise him up at the last day."} ]
        , 55: [ {class: "vs", wj: true, text: "For my flesh is food indeed, and my blood is drink indeed."} ]
        , 56: [ {class: "vs", wj: true, text: "He who eats my flesh and drinks my blood lives in me, and I in him."} ]
        , 57: [ {class: "vs", wj: true, text: "As the living Father sent me, and I live because of the Father; so he who feeds on me, he will also live because of me."} ]
        , 58: [ {class: "vs", wj: true, text: "This is the bread which came down out of heaven—not as our fathers ate the manna, and died. He who eats this bread will live forever.”"} ]
        , 59: [ {class: "vs", text: "He said these things in the synagogue, as he taught in Capernaum."} ]
        , 60: [ {class: "vs", text: "Therefore many of his disciples, when they heard this, said, “This is a hard saying! Who can listen to it?”"} ]
        , 61: [ {class: "endpara", text: "But Jesus knowing in himself that his disciples murmured at this, said to them,"}
              , {class: "vs", wj: true, text: "“Does this cause you to stumble?"}
              ]
        , 62: [ {class: "vs", wj: true, text: "Then what if you would see the Son of Man ascending to where he was before?"} ]
        , 63: [ {class: "vs", wj: true, text: "It is the spirit who gives life. The flesh profits nothing. The words that I speak to you are spirit, and are life."} ]
        , 64: [ {class: "vs", wj: true, text: "But there are some of you who don’t believe.”</span> For Jesus knew from the beginning who they were who didn’t believe, and who it was who would betray him."} ]
        , 65: [ {class: "endpara", text: "He said,"}
              , {class: "vs", wj: true, text: "“For this cause I have said to you that no one can come to me, unless it is given to him by my Father.”"}
              ]
        , 66: [ {class: "vs", text: "At this, many of his disciples went back, and walked no more with him."} ]
        , 67: [ {class: "endpara", text: "Jesus said therefore to the twelve,"}
              , {class: "vs", wj: true, text: "“You don’t also want to go away, do you?”"}
              ]
        , 68: [ {class: "vs", text: "Simon Peter answered him, “Lord, to whom would we go? You have the words of eternal life."} ]
        , 69: [ {class: "vs", text: "We have come to believe and know that you are the Christ, the Son of the living God.”"} ]
        , 70: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Didn’t I choose you, the twelve, and one of you is a devil?”"}
              ]
        , 71: [ {class: "vs", text: "Now he spoke of Judas, the son of Simon Iscariot, for it was he who would betray him, being one of the twelve."} ]
        }
  , 7:  { 1:  [ {class: "vs", text: "After these things, Jesus was walking in Galilee, for he wouldn’t walk in Judea, because the Jews sought to kill him."} ]
        , 2:  [ {class: "vs", text: "Now the feast of the Jews, the Feast of Booths, was at hand."} ]
        , 3:  [ {class: "vs", text: "His brothers therefore said to him, “Depart from here and go into Judea, that your disciples also may see your works which you do."} ]
        , 4:  [ {class: "vs", text: "For no one does anything in secret while he seeks to be known openly. If you do these things, reveal yourself to the world.”"} ]
        , 5:  [ {class: "vs", text: "For even his brothers didn’t believe in him."} ]
        , 6:  [ {class: "endpara", text: "Jesus therefore said to them,"}
              , {class: "vs", wj: true, text: "“My time has not yet come, but your time is always ready."}
              ]
        , 7:  [ {class: "vs", wj: true, text: "The world can’t hate you, but it hates me, because I testify about it, that its works are evil."} ]
        , 8:  [ {class: "vs", wj: true, text: "You go up to the feast. I am not yet going up to this feast, because my time is not yet fulfilled.”"} ]
        , 9:  [ {class: "vs", text: "Having said these things to them, he stayed in Galilee."} ]
        , 10: [ {class: "vs", text: "But when his brothers had gone up to the feast, then he also went up, not publicly, but as it were in secret."} ]
        , 11: [ {class: "vs", text: "The Jews therefore sought him at the feast, and said, “Where is he?”"} ]
        , 12: [ {class: "vs", text: "There was much murmuring among the multitudes concerning him. Some said, “He is a good man.” Others said, “Not so, but he leads the multitude astray.”"} ]
        , 13: [ {class: "vs", text: "Yet no one spoke openly of him for fear of the Jews."} ]
        , 14: [ {class: "vs", text: "But when it was now the middle of the feast, Jesus went up into the temple and taught."} ]
        , 15: [ {class: "vs", text: "The Jews therefore marveled, saying, “How does this man know letters, having never been educated?”"} ]
        , 16: [ {class: "endpara", text: "Jesus therefore answered them,"}
              , {class: "vs", wj: true, text: "“My teaching is not mine, but his who sent me."}
              ]
        , 17: [ {class: "vs", wj: true, text: "If anyone desires to do his will, he will know about the teaching, whether it is from God, or if I am speaking from myself."} ]
        , 18: [ {class: "vs", wj: true, text: "He who speaks from himself seeks his own glory, but he who seeks the glory of him who sent him is true, and no unrighteousness is in him."} ]
        , 19: [ {class: "vs", wj: true, text: "Didn’t Moses give you the law, and yet none of you keeps the law? Why do you seek to kill me?”"} ]
        , 20: [ {class: "vs", text: "The multitude answered, “You have a demon! Who seeks to kill you?”"} ]
        , 21: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“I did one work and you all marvel because of it."}
              ]
        , 22: [ {class: "vs", wj: true, text: "Moses has given you circumcision (not that it is of Moses, but of the fathers), and on the Sabbath you circumcise a boy."} ]
        , 23: [ {class: "vs", wj: true, text: "If a boy receives circumcision on the Sabbath, that the law of Moses may not be broken, are you angry with me, because I made a man completely healthy on the Sabbath?"} ]
        , 24: [ {class: "vs", wj: true, text: "Don’t judge according to appearance, but judge righteous judgment.”"} ]
        , 25: [ {class: "vs", text: "Therefore some of them of Jerusalem said, “Isn’t this he whom they seek to kill?"} ]
        , 26: [ {class: "vs", text: "Behold, he speaks openly, and they say nothing to him. Can it be that the rulers indeed know that this is truly the Christ?"} ]
        , 27: [ {class: "vs", text: "However we know where this man comes from, but when the Christ comes, no one will know where he comes from.”"} ]
        , 28: [ {class: "endpara", text: "Jesus therefore cried out in the temple, teaching and saying,"}
              , {class: "vs", wj: true, text: "“You both know me, and know where I am from. I have not come of myself, but he who sent me is true, whom you don’t know."}
              ]
        , 29: [ {class: "vs", wj: true, text: "I know him, because I am from him, and he sent me.”"} ]
        , 30: [ {class: "vs", text: "They sought therefore to take him; but no one laid a hand on him, because his hour had not yet come."} ]
        , 31: [ {class: "vs", text: "But of the multitude, many believed in him. They said, “When the Christ comes, he won’t do more signs than those which this man has done, will he?”"} ]
        , 32: [ {class: "vs", text: "The Pharisees heard the multitude murmuring these things concerning him, and the chief priests and the Pharisees sent officers to arrest him."} ]
        , 33: [ {class: "endpara", text: "Then Jesus said,"}
              , {class: "vs", wj: true, text: "“I will be with you a little while longer, then I go to him who sent me."}
              ]
        , 34: [ {class: "vs", wj: true, text: "You will seek me, and won’t find me. You can’t come where I am.”"} ]
        , 35: [ {class: "vs", text: "The Jews therefore said among themselves, “Where will this man go that we won’t find him? Will he go to the Dispersion among the Greeks, and teach the Greeks?"} ]
        , 36: [ {class: "endpara", text: "What is this word that he said,"}
              , {class: "vs", wj: true, text: "‘You will seek me, and won’t find me;’</span> and"}
              , {class: "vs", wj: true, text: "‘Where I am, you can’t come’</span> ?”"}
              ]
        , 37: [ {class: "endpara", text: "Now on the last and greatest day of the feast, Jesus stood and cried out,"}
              , {class: "vs", wj: true, text: "“If anyone is thirsty, let him come to me and drink!"}
              ]
        , 38: [ {class: "vs", wj: true, text: "He who believes in me, as the Scripture has said, from within him will flow rivers of living water.”"} ]
        , 39: [ {class: "vs", text: "But he said this about the Spirit, which those believing in him were to receive. For the Holy Spirit was not yet given, because Jesus wasn’t yet glorified."} ]
        , 40: [ {class: "vs", text: "Many of the multitude therefore, when they heard these words, said, “This is truly the prophet.”"} ]
        , 41: [ {class: "vs", text: "Others said, “This is the Christ.” But some said, “What, does the Christ come out of Galilee?"} ]
        , 42: [ {class: "vs", text: "Hasn’t the Scripture said that the Christ comes of the offspring of David, and from Bethlehem, the village where David was?”"} ]
        , 43: [ {class: "vs", text: "So a division arose in the multitude because of him."} ]
        , 44: [ {class: "vs", text: "Some of them would have arrested him, but no one laid hands on him."} ]
        , 45: [ {class: "vs", text: "The officers therefore came to the chief priests and Pharisees, and they said to them, “Why didn’t you bring him?”"} ]
        , 46: [ {class: "vs", text: "The officers answered, “No man ever spoke like this man!”"} ]
        , 47: [ {class: "vs", text: "The Pharisees therefore answered them, “You aren’t also led astray, are you?"} ]
        , 48: [ {class: "vs", text: "Have any of the rulers believed in him, or of the Pharisees?"} ]
        , 49: [ {class: "vs", text: "But this multitude that doesn’t know the law is cursed.”"} ]
        , 50: [ {class: "vs", text: "Nicodemus (he who came to him by night, being one of them) said to them,"} ]
        , 51: [ {class: "vs", text: "“Does our law judge a man, unless it first hears from him personally and knows what he does?”"} ]
        , 52: [ {class: "vs", text: "They answered him, “Are you also from Galilee? Search, and see that no prophet has arisen out of Galilee.”"} ]
        , 53: [ {class: "vs", text: "Everyone went to his own house,"} ]
        }
  , 8:  { 1:  [ {class: "vs", text: "but Jesus went to the Mount of Olives."} ]
        , 2:  [ {class: "vs", text: "Now very early in the morning, he came again into the temple, and all the people came to him. He sat down and taught them."} ]
        , 3:  [ {class: "vs", text: "The scribes and the Pharisees brought a woman taken in adultery. Having set her in the middle,"} ]
        , 4:  [ {class: "vs", text: "they told him, “Teacher, we found this woman in adultery, in the very act."} ]
        , 5:  [ {class: "vs", text: "Now in our law, Moses commanded us to stone such women. What then do you say about her?”"} ]
        , 6:  [ {class: "vs", text: "They said this testing him, that they might have something to accuse him of.</p><p> But Jesus stooped down and wrote on the ground with his finger."} ]
        , 7:  [ {class: "endpara", text: "But when they continued asking him, he looked up and said to them,"}
              , {class: "vs", wj: true, text: "“He who is without sin among you, let him throw the first stone at her.”"}
              ]
        , 8:  [ {class: "vs", text: "Again he stooped down and wrote on the ground with his finger."} ]
        , 9:  [ {class: "vs", text: "They, when they heard it, being convicted by their conscience, went out one by one, beginning from the oldest, even to the last. Jesus was left alone with the woman where she was, in the middle."} ]
        , 10: [ {class: "endpara", text: "Jesus, standing up, saw her and said,"}
              , {class: "vs", wj: true, text: "“Woman, where are your accusers? Did no one condemn you?”"}
              ]
        , 11: [ {class: "endpara", text: "She said, “No one, Lord.”</p><p> Jesus said,"}
              , {class: "vs", wj: true, text: "“Neither do I condemn you. Go your way. From now on, sin no more.”"}
              ]
        , 12: [ {class: "endpara", text: "Again, therefore, Jesus spoke to them, saying,"}
              , {class: "vs", wj: true, text: "“I am the light of the world.</span>"}
              , {class: "vs", wj: true, text: "He who follows me will not walk in the darkness, but will have the light of life.”"}
              ]
        , 13: [ {class: "vs", text: "The Pharisees therefore said to him, “You testify about yourself. Your testimony is not valid.”"} ]
        , 14: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Even if I testify about myself, my testimony is true, for I know where I came from, and where I am going; but you don’t know where I came from, or where I am going."}
              ]
        , 15: [ {class: "vs", wj: true, text: "You judge according to the flesh. I judge no one."} ]
        , 16: [ {class: "vs", wj: true, text: "Even if I do judge, my judgment is true, for I am not alone, but I am with the Father who sent me."} ]
        , 17: [ {class: "vs", wj: true, text: "It’s also written in your law that the testimony of two people is valid."} ]
        , 18: [ {class: "vs", wj: true, text: "I am one who testifies about myself, and the Father who sent me testifies about me.”"} ]
        , 19: [ {class: "", text: "They said therefore to him, “Where is your Father?”</p><p> Jesus answered,"}
              , {class: "vs", wj: true, text: "“You know neither me nor my Father. If you knew me, you would know my Father also.”"}
              ]
        , 20: [ {class: "vs", text: "Jesus spoke these words in the treasury, as he taught in the temple. Yet no one arrested him, because his hour had not yet come."} ]
        , 21: [ {class: "endpara", text: "Jesus said therefore again to them,"}
              , {class: "vs", wj: true, text: "“I am going away, and you will seek me, and you will die in your sins. Where I go, you can’t come.”"}
              ]
        , 22: [ {class: "endpara", text: "The Jews therefore said, “Will he kill himself, because he says,"}
              , {class: "vs", wj: true, text: "‘Where I am going, you can’t come’</span> ?”"}
              ]
        , 23: [ {class: "endpara", text: "He said to them,"}
              , {class: "vs", wj: true, text: "“You are from beneath. I am from above. You are of this world. I am not of this world."}
              ]
        , 24: [ {class: "vs", wj: true, text: "I said therefore to you that you will die in your sins; for unless you believe that I am"}
              , {class: "vs", wj: true, text: "he, you will die in your sins.”"}
              ]
        , 25: [ {class: "endpara", text: "They said therefore to him, “Who are you?”</p><p> Jesus said to them,"}
              , {class: "vs", wj: true, text: "“Just what I have been saying to you from the beginning."}
              ]
        , 26: [ {class: "vs", wj: true, text: "I have many things to speak and to judge concerning you. However he who sent me is true; and the things which I heard from him, these I say to the world.”"} ]
        , 27: [ {class: "vs", text: "They didn’t understand that he spoke to them about the Father."} ]
        , 28: [ {class: "endpara", text: "Jesus therefore said to them,"}
              , {class: "vs", wj: true, text: "“When you have lifted up the Son of Man, then you will know that I am he, and I do nothing of myself, but as my Father taught me, I say these things."}
              ]
        , 29: [ {class: "vs", wj: true, text: "He who sent me is with me. The Father hasn’t left me alone, for I always do the things that are pleasing to him.”"} ]
        , 30: [ {class: "vs", text: "As he spoke these things, many believed in him."} ]
        , 31: [ {class: "", text: "Jesus therefore said to those Jews who had believed him,"}
              , {class: "vs", wj: true, text: "“If you remain in my word, then you are truly my disciples."}
              ]
        , 32: [ {class: "vs", wj: true, text: "You will know the truth, and the truth will make you free.”"} ]
        , 33: [ {class: "endpara", text: "They answered him, “We are Abraham’s offspring, and have never been in bondage to anyone. How do you say,"}
              , {class: "vs", wj: true, text: "‘You will be made free’</span> ?”"}
              ]
        , 34: [ {class: "endpara", text: "Jesus answered them,"}
              , {class: "vs", wj: true, text: "“Most certainly I tell you, everyone who commits sin is the bondservant of sin."}
              ]
        , 35: [ {class: "vs", wj: true, text: "A bondservant doesn’t live in the house forever. A son remains forever."} ]
        , 36: [ {class: "vs", wj: true, text: "If therefore the Son makes you free, you will be free indeed."} ]
        , 37: [ {class: "vs", wj: true, text: "I know that you are Abraham’s offspring, yet you seek to kill me, because my word finds no place in you."} ]
        , 38: [ {class: "vs", wj: true, text: "I say the things which I have seen with my Father; and you also do the things which you have seen with your father.”"} ]
        , 39: [ {class: "endpara", text: "They answered him, “Our father is Abraham.”</p><p> Jesus said to them,"}
              , {class: "vs", wj: true, text: "“If you were Abraham’s children, you would do the works of Abraham."}
              ]
        , 40: [ {class: "vs", wj: true, text: "But now you seek to kill me, a man who has told you the truth which I heard from God. Abraham didn’t do this."} ]
        , 41: [ {class: "vs", wj: true, text: "You do the works of your father.”</span></p><p> They said to him, “We were not born of sexual immorality. We have one Father, God.”"} ]
        , 42: [ {class: "endpara", text: "Therefore Jesus said to them,"}
              , {class: "vs", wj: true, text: "“If God were your father, you would love me, for I came out and have come from God. For I haven’t come of myself, but he sent me."}
              ]
        , 43: [ {class: "vs", wj: true, text: "Why don’t you understand my speech? Because you can’t hear my word."} ]
        , 44: [ {class: "vs", wj: true, text: "You are of your father, the devil, and you want to do the desires of your father. He was a murderer from the beginning, and doesn’t stand in the truth, because there is no truth in him. When he speaks a lie, he speaks on his own; for he is a liar, and the father of lies."} ]
        , 45: [ {class: "vs", wj: true, text: "But because I tell the truth, you don’t believe me."} ]
        , 46: [ {class: "vs", wj: true, text: "Which of you convicts me of sin? If I tell the truth, why do you not believe me?"} ]
        , 47: [ {class: "vs", wj: true, text: "He who is of God hears the words of God. For this cause you don’t hear, because you are not of God.”"} ]
        , 48: [ {class: "vs", text: "Then the Jews answered him, “Don’t we say well that you are a Samaritan, and have a demon?”"} ]
        , 49: [ {class: "endpara", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“I don’t have a demon, but I honor my Father and you dishonor me."}
              ]
        , 50: [ {class: "vs", wj: true, text: "But I don’t seek my own glory. There is one who seeks and judges."} ]
        , 51: [ {class: "vs", wj: true, text: "Most certainly, I tell you, if a person keeps my word, he will never see death.”"} ]
        , 52: [ {class: "endpara", text: "Then the Jews said to him, “Now we know that you have a demon. Abraham died, as did the prophets; and you say,"}
              , {class: "vs", wj: true, text: "‘If a man keeps my word, he will never taste of death.’"}
              ]
        , 53: [ {class: "vs", text: "Are you greater than our father, Abraham, who died? The prophets died. Who do you make yourself out to be?”"} ]
        , 54: [ {class: "endpara", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“If I glorify myself, my glory is nothing. It is my Father who glorifies me, of whom you say that he is our God."}
              ]
        , 55: [ {class: "vs", wj: true, text: "You have not known him, but I know him. If I said, ‘I don’t know him,’ I would be like you, a liar. But I know him and keep his word."} ]
        , 56: [ {class: "vs", wj: true, text: "Your father Abraham rejoiced to see my day. He saw it, and was glad.”"} ]
        , 57: [ {class: "vs", text: "The Jews therefore said to him, “You are not yet fifty years old! Have you seen Abraham?”"} ]
        , 58: [ {class: "endpara", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“Most certainly, I tell you, before Abraham came into existence, I AM.</span>"}
              , {class: "vs", wj: true, text: "”"}
              ]
        , 59: [ {class: "vs", text: "Therefore they took up stones to throw at him, but Jesus was hidden, and went out of the temple, having gone through the middle of them, and so passed by."} ]
        }
  , 9:  { 1:  [ {class: "vs", text: "As he passed by, he saw a man blind from birth."} ]
        , 2:  [ {class: "vs", text: "His disciples asked him, “Rabbi, who sinned, this man or his parents, that he was born blind?”"} ]
        , 3:  [ {class: "endpara", text: "Jesus answered,"}
              , {class: "vs", wj: true, text: "“This man didn’t sin, nor did his parents; but, that the works of God might be revealed in him."}
              ]
        , 4:  [ {class: "vs", wj: true, text: "I must work the works of him who sent me while it is day. The night is coming, when no one can work."} ]
        , 5:  [ {class: "vs", wj: true, text: "While I am in the world, I am the light of the world.”"} ]
        , 6:  [ {class: "vs", text: "When he had said this, he spat on the ground, made mud with the saliva, anointed the blind man’s eyes with the mud,"} ]
        , 7:  [ {class: "endpara", text: "and said to him,"}
              , {class: "vs", wj: true, text: "“Go, wash in the pool of Siloam”</span> (which means “Sent”). So he went away, washed, and came back seeing."}
              ]
        , 8:  [ {class: "vs", text: "The neighbors therefore, and those who saw that he was blind before, said, “Isn’t this he who sat and begged?”"} ]
        , 9:  [ {class: "endpara", text: "Others were saying, “It is he.” Still others were saying, “He looks like him.”"}
              , {class: "endpara", text: "He said, “I am he.”"}
              ]
        , 10: [ {class: "vs", text: "They therefore were asking him, “How were your eyes opened?”"} ]
        , 11: [ {class: "endpara", text: "He answered, “A man called Jesus made mud, anointed my eyes, and said to me,"}
              , {class: "vs", wj: true, text: "‘Go to the pool of Siloam and wash.’</span> So I went away and washed, and I received sight.”"}
              ]
        , 12: [ {class: "endpara", text: "Then they asked him, “Where is he?”"}
              , {class: "endpara", text: "He said, “I don’t know.”"}
              ]
        , 13: [ {class: "vs", text: "They brought him who had been blind to the Pharisees."} ]
        , 14: [ {class: "vs", text: "It was a Sabbath when Jesus made the mud and opened his eyes."} ]
        , 15: [ {class: "vs", text: "Again therefore the Pharisees also asked him how he received his sight. He said to them, “He put mud on my eyes, I washed, and I see.”"} ]
        , 16: [ {class: "vs", text: "Some therefore of the Pharisees said, “This man is not from God, because he doesn’t keep the Sabbath.” Others said, “How can a man who is a sinner do such signs?” There was division among them."} ]
        , 17: [ {class: "endpara", text: "Therefore they asked the blind man again, “What do you say about him, because he opened your eyes?”"}
              , {class: "endpara", text: "He said, “He is a prophet.”"}
              ]
        , 18: [ {class: "vs", text: "The Jews therefore didn’t believe concerning him, that he had been blind, and had received his sight, until they called the parents of him who had received his sight,"} ]
        , 19: [ {class: "vs", text: "and asked them, “Is this your son, whom you say was born blind? How then does he now see?”"} ]
        , 20: [ {class: "vs", text: "His parents answered them, “We know that this is our son, and that he was born blind;"} ]
        , 21: [ {class: "vs", text: "but how he now sees, we don’t know; or who opened his eyes, we don’t know. He is of age. Ask him. He will speak for himself.”"} ]
        , 22: [ {class: "vs", text: "His parents said these things because they feared the Jews; for the Jews had already agreed that if any man would confess him as Christ, he would be put out of the synagogue."} ]
        , 23: [ {class: "vs", text: "Therefore his parents said, “He is of age. Ask him.”"} ]
        , 24: [ {class: "vs", text: "So they called the man who was blind a second time, and said to him, “Give glory to God. We know that this man is a sinner.”"} ]
        , 25: [ {class: "vs", text: "He therefore answered, “I don’t know if he is a sinner. One thing I do know: that though I was blind, now I see.”"} ]
        , 26: [ {class: "vs", text: "They said to him again, “What did he do to you? How did he open your eyes?”"} ]
        , 27: [ {class: "vs", text: "He answered them, “I told you already, and you didn’t listen. Why do you want to hear it again? You don’t also want to become his disciples, do you?”"} ]
        , 28: [ {class: "vs", text: "They insulted him and said, “You are his disciple, but we are disciples of Moses."} ]
        , 29: [ {class: "vs", text: "We know that God has spoken to Moses. But as for this man, we don’t know where he comes from.”"} ]
        , 30: [ {class: "vs", text: "The man answered them, “How amazing! You don’t know where he comes from, yet he opened my eyes."} ]
        , 31: [ {class: "vs", text: "We know that God doesn’t listen to sinners, but if anyone is a worshiper of God, and does his will, he listens to him."} ]
        , 32: [ {class: "vs", text: "Since the world began it has never been heard of that anyone opened the eyes of someone born blind."} ]
        , 33: [ {class: "vs", text: "If this man were not from God, he could do nothing.”"} ]
        , 34: [ {class: "vs", text: "They answered him, “You were altogether born in sins, and do you teach us?” Then they threw him out."} ]
        , 35: [ {class: "endpara", text: "Jesus heard that they had thrown him out, and finding him, he said,"}
              , {class: "vs", wj: true, text: "“Do you believe in the Son of God?”"}
              ]
        , 36: [ {class: "vs", text: "He answered, “Who is he, Lord, that I may believe in him?”"} ]
        , 37: [ {class: "endpara", text: "Jesus said to him,"}
              , {class: "vs", wj: true, text: "“You have both seen him, and it is he who speaks with you.”"}
              ]
        , 38: [ {class: "vs", text: "He said, “Lord, I believe!” and he worshiped him."} ]
        , 39: [ {class: "vs", wj: true, text: "Jesus said,"}
              , {class: "vs", wj: true, text: "“I came into this world for judgment, that those who don’t see may see; and that those who see may become blind.”"}
              ]
        , 40: [ {class: "vs", text: "Those of the Pharisees who were with him heard these things, and said to him, “Are we also blind?”"} ]
        , 41: [ {class: "endpara", text: "Jesus said to them,"}
              , {class: "vs", wj: true, text: "“If you were blind, you would have no sin; but now you say, ‘We see.’ Therefore your sin remains."}
              ]
        }
  }