        , 41: [ {class: "vs", text: "Uriah the Hittite, Zabad the son of Ahlai,"} ]
        , 42: [ {class: "vs", text: "Adina the son of Shiza the Reubenite, a chief of the Reubenites, and thirty with him,"} ]
