        , 78: [ {class: "vs", text: "On the twelfth day Ahira the son of Enan, prince of the children of Naphtali,"} ]
        , 79: [ {class: "vs", text: "gave his offering:</p><p> one silver platter, the weight of which was one hundred thirty shekels,</p><p> one silver bowl of seventy shekels, according to the shekel of the sanctuary; both of them full of fine flour mixed with oil for a meal offering;"} ]
        , 80: [ {class: "vs", text: "one golden spoon of ten shekels, full of incense;"} ]
